/** @module Define */


/**
 * @classdesc
 *
 * Our define class, contains all magic dnumber defines
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * // DEFINE = new Define();
 *
 */
export default class Define {
	constructor() {
		/**
		 * Queue state of operations
		 */
		this.STATUS_LOADING = 0;
		this.STATUS_LOADED = 1;
		this.STATUS_RUNNING = 2;
		this.STATUS_ERROR = 3;

		/**
		 * Render modes
		 */
		this.RENDER_INSERT = "insert";
		this.RENDER_APPEND = "append";
		this.RENDER_REPLACE = "replace";


		/**
		 *  Command types
		 */

		this.COMMAND_INSTANT = "Instant";
		this.COMMAND_EVENT = "Event";
		this.COMMAND_SUB = "Sub";

		/**
		 *  Queue states
		 */
		this.QUEUE_ADDED = 0;
		this.QUEUE_RUNNING = 1;
		this.QUEUE_FINISHED = 2;
		this.QUEUE_ERROR = 3;

		/**
		 * Queueable Finished states
		 */

		this.FIN_OK = 0;
		this.FIN_WARNING = 1;
		this.FIN_ERROR = 2;

		this.MEMORY_GARBAGE="Garbage";
		this.MEMORY_SESSION="Session";
		this.MEMORY_PERMANENT="Permanent";


		/**
		 *  Keycodes
		 */

		this.KEY_RETURN=13;

	}
}

/**
 * @module ourthings
 */

export {default as Queue} from './Queue';
export {default as Queueable} from './Queueable';

/** @module Language */


/**
 * @classdesc
 *
 * Memory manager class
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * // memory = new Language();
 *
 */
export default class Language {
	/**
	 * Add new lang def
	 * @param defs
	 * @param langId
	 */
	constructor(defs,langId) {
		this.langId=langId||0;
		this.defs=defs;
	}

	/**
	 * Get the tag in the current language#
	 * @param tag
	 * @return {*}
	 */
	get(tag) {
		return this.defs.tags[tag][this.langId];
	}

	/**
	 * Set the current language
	 * @param langId
	 * @return {*}
	 */
	setLanguage(langId) {
		this.langId=langId||0;
	}
};

import Queue from './Queue';

/**
 *  Import all the queueables we want to use here. Make sure to add an entry to to new queue to map the commands
 *
 */
import Internals from './Queueable/Internals';
import Templates from './Queueable/Templates';
import Elements from './Queueable/Elements';
import Api from './Queueable/Api';
import Openlayers from './Queueable/Openlayers';
import Browser from './Queueable/Browser';
import W3Menu from './Queueable/W3Menu';
import Mapbox from "./Queueable/Mapbox";
import Markdown from "./Queueable/Markdown";

/*
 * Start the queue engine
 */
let queue;
/*
 * Wait until the dom is loaded (IE11 will not have the dom in place before it starts running scripts
 */
document.addEventListener("DOMContentLoaded", function(event) {
	queue = new Queue({
		"internals": Internals,
		"templates": Templates,
		"elements": Elements,
		"api": Api,
		"openlayers": Openlayers,
		"browser": Browser,
		"w3menu": W3Menu,
		"mapbox" : Mapbox,
		"markdown": Markdown
	});
	window.queue = queue;
});

/** @module Memory */


/**
 * @classdesc
 *
 * Memory manager class
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * // memory = new Memory();
 *
 */
export default class Memory {
	/**
	 * Add new memory element
	 * @param pid
	 * @param mode
	 * @param origin
	 * @param value
	 */
	constructor(pid,mode,origin,value) {
		this.pid=pid;
		this.mode= mode;
		this.origin=origin;
		this.value=value;
	}

	/**
	 * Get the current value of the memory item (same as memory.name.value)
	 * @return {*}
	 */
	get() {
		return this.value;
	}

	/**
	 * Toggle the value based on an array list of values
	 * @param {array} values
	 * @return {*}
	 */
	toggle(values) {
		let currentValue=this.value;
		let len=values.length;
		let pos=values.indexOf(this.value);
		pos++;
		if(pos>=len)
			pos=0;
		this.value=values[pos];
		return currentValue;
	}

	/**
	 * Return an encoded version of the memory for storage in a coookie
	 * @returns {string}
	 * @private
	 */
	_store() {
		return window.btoa(JSON.stringify({
			pid: this.pid,
			mode: this.mode,
			origin: this.origin,
			value: this.value
		}));
	}
};

/** @module ourthings/Queue */
import Define from './Define.js';
import * as consoleBadge from 'console-badge';
import Memory from "./Memory";
import Language from "./Language";

/**
 * @classdesc
 *
 * The main queue class
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * let queue = new Queue();
 */
class Queue {

	/**
	 * Class constructor
	 */
	constructor(queueablesList) {

		let self = this;

		/*
		 * Create our DEFINE object for
		 * @type {Define}
		 */
		self.DEFINE = new Define();
		/*
		 * Our Queue array
		 *
		 * @type {Array}
		 */
		self.queue = {};

		/*
		 * Our events (queues) which can be called by name
		 */
		self.prepare = {};


		/*
		 * Queueable items object
		 */
		self.queueables = {};
		/*
		 * Templates to be loaded
		 * @type {Array}
		 */
		self.templates = [];

		/*
		 * Register lists
		 */
		self.registers = [];

		/*
		 * Create a fragment for big dom inserts
		 * @type {DocumentFragment}
		 */
		self.fragment = document.createDocumentFragment();

		/*
		 * Se our status
		 * @type {number}
		 */
		self.status = self.DEFINE.STATUS_LOADING;

		/*
		 * Our queue process ID
		 * @type {number}
		 */
		self.pid = 0;
		self.runningPid = -1;

		/*
		 * Our unique bind ids
		 */
		self.ucid = 0;

		/*
		 * When processing out loops this keeps a track
		 */
		self.activeLoops = [];


		/*
		 * Language support
		 */

		self.language=false;

		/*
		 * Default time for process to be executed after
		 * TODO Platform test / tune
		 * @type {number}
		 */
		self.defaultTimer = 10;

		/*
		 * Dev mode?
		 */

		self.developerMode = false;

		console.clear();
		consoleBadge.log({
			mode: 'shields.io',
			leftText: 'ourthings framework',
			rightText: 'https://github.com/nautoguide/ourthings ðŸš€',
			rightBgColor: '#ffc107',
			rightTextColor: '#1a1a1a'
		});
		/*
		 * Listener for our chrome-plugin
		 */
		document.addEventListener('ourthings_site', function (e) {
			switch (e.detail.function) {
				case 'ping':
					document.dispatchEvent(new CustomEvent('ourthings_extension', {
						detail: {"function": "pong"}
					}));
					break;
				case 'menu':
					self.menu();
					break;
			}
		});

		self.browserClasses();

		/*
		 * Run init against all our queueables
		 *
		 * This basically passes the queue object (self) though but also for any queueables that require it
		 * starts any promise functions that will result in them becoming active
		 */
		for (let i in queueablesList) {
			self.checkQueueable(i, queueablesList[i]);
		}

		/*
		 * Initialise the memory
		 */
		window.memory = {};

		/*
		 * Load any perm cookies
		 */
		self._loadMemoryPerms();
		if (window.memory.developer && window.memory.developer.value === true)
			self.developerMode = true;
		/*
		 * Load any url params into memoery
		 */

		self.setMemory("urlParams", self.urlToJson(), "Session");

		/*
		 * Enable language support?
		 */

		let attr= self.getElement("script[data-lang]", false);
		if(attr) {
			let langFile=attr.getAttribute("data-lang");
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Booting',
				rightText: "Language support",
				rightBgColor: '#ffc107',
				rightTextColor: '#1a1a1a'
			});
			fetch(langFile, {
				headers: {
					'Content-Type': 'application/json'
				}
			})
				.then(response => self.handleFetchErrors(response))
				.then(response => response.json())
				.then(function (response) {
					/**
					 * Convert the response to json and start the loader
					 */
					self.language= new Language(response);
					bootTemplates();
				})
				.catch(function (error) {
					self.reportError(error, 'Warning this error is fatal, I could not load your language file');
				});
		} else {
			bootTemplates();
		}


		function bootTemplates() {
			/*
			 * Load the templates.json
			 *
			 * This can now be specified by data-templates on the script include
			 */
			let templateInclude = "templates.json";

			let attr = self.getElement("script[data-templates]", false);
			if (attr && attr.getAttribute("data-templates")) {
				templateInclude = attr.getAttribute("data-templates");
			}

			fetch(templateInclude, {
				headers: {
					'Content-Type': 'application/json'
				}
			})
				.then(response => self.handleFetchErrors(response))
				.then(response => response.json())
				.then(function (response) {
					/**
					 * Convert the response to json and start the loader
					 */
					self.templates = response;
					self.templateLoader();
				})
				.catch(function (error) {
					self.reportError(error, 'Warning this error is probably fatal as I have no templates to load');
				});
		}
	}

	/**
	 * Error Handler for fetch calls
	 * @param response {object} - Fetch response object
	 * @returns {Object}
	 */
	handleFetchErrors(response) {
		let self = this;
		if (!response.ok) {
			self.status = self.DEFINE.STATUS_ERROR;
			throw Error(response.statusText);
		}
		return response;
	}

	checkQueueable(name, obj) {
		let self = this;
		if (self.queueables[name] === undefined) {
			self.queueables[name] = new obj();
			self.queueables[name].init(self);
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Booting',
				rightText: name,
				rightBgColor: '#ffc107',
				rightTextColor: '#1a1a1a'
			});
			return false;
		}
		return true;
	}

	/**
	 * Helper function to upcase first letter of string
	 * @param string
	 * @return {string}
	 */
	capitalizeFirstLetter(string) {
		return string.charAt(0).toUpperCase() + string.slice(1);
	}

	/**
	 * Loads templates from the template stack. Recursively calls self until stack is empty
	 * @returns {void}
	 */
	templateLoader() {
		let self = this;

		/*
		 * Version check as we changed the format
		 */
		if (this.templates.version < 1.0) {
			self.reportError("Template file has no version, expecting >=1.0", "Warning this error is probably fatal as I have no templates to load");
			return;
		}
		/*
		 *  Are there any templates to load?
		 *
		 *  If not then we dump the fragment into the dom
		 */
		if (this.templates.templates.length === 0) {
			document.head.appendChild(self.fragment);
			// Clean up the fragment
			self.fragment = document.createDocumentFragment();
			/*
			 * Set our status and then process the init template
			 */
			self.status = self.DEFINE.STATUS_LOADED;
			/*
			 * Detach the init template loader from this stack chain
			 *
			 * We do this because a fail in a subsequent template will register as an error in the fetch catch method
			 * which is misleading
			 */
			setTimeout(function () {
				self.templateProcessor("#init", false);
			}, 100);

			self.status = self.DEFINE.STATUS_RUNNING;
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Online',
				rightText: 'queue.show(); # To debug the queue',
				rightBgColor: '#ffc107',
				rightTextColor: '#1a1a1a'
			});
			if (self.developerMode === true) {
				consoleBadge.log({
					mode: 'shields.io',
					leftText: 'DEVELOPER MODE',
					rightText: 'Enabled',
					rightBgColor: '#ffc107',
					rightTextColor: '#1a1a1a'
				});
			}
			return;
		}

		/*
	    * Pop the template off the stack
	    */
		let template = this.templates.templates.pop();
		if (typeof template === "string") {
			template = {"url": template, "type": "text/html"}
		}

		/*
		 * Is there a cache?
		 */
		if (this.templates.cache && self.developerMode !== true) {
			template = this.templates.cache;
			this.templates.templates = [];
		}

		fetch(template.url, {
			headers: {
				'Content-Type': template.type
			},
			mode: 'cors',
			credentials: 'same-origin'
		})
			.then(response => self.handleFetchErrors(response))
			.then(response => response.text())
			.then(function (response) {

				/*
				 * Get the template we were sent and add it to the fragment for insertion into the dom
				 *
				 * We wrap it in meta tag, this helps improve render speed but still stuck with an innerHTML
				 * as we don't know the content
				 *
				 */
				let text = response;
				/*
				 * Remove any html comments as they will slow down processing later on
				 */
				text = text.replace(/<!--([\s\S]*?)-->/g, '');

				let meta = document.createElement('meta');
				meta.setAttribute("name", "generator");
				meta.setAttribute("content", template.url);
				if (template.type === 'text/css')
					meta.innerHTML = `<style>${text}</style>`;
				else
					meta.innerHTML = text;
				self.fragment.appendChild(meta);

				/*
				 *  Call our self again to process any more templates
				 */
				self.templateLoader();
			})
			.catch(function (error) {
				console.error('Error:', error);
				console.info("Warning this error is probably fatal as a template specified in templates.json has failed to load or wont process");
			});
	};

	/**
	 * Template processor
	 * Takes a template, process it and places into the dom
	 * @param templateId {string} - ID of the template
	 * @param targetId {string|false} - Place in the dom the put the result. In the event of false we process without dom
	 * @return {boolean|string} - success status
	 */
	templateProcessor(templateId, targetId, mode) {
		let self = this;
		let commands = [];
		mode = mode || self.DEFINE.RENDER_INSERT;

		let templateDom = self.getElement(templateId);
		if (!templateDom) {
			self.reportError('No valid template', 'I have no valid template, check the templateId [' + templateId + ']');
			return false;
		}
		let targetDom = undefined;
		let templateHTML = templateDom.innerHTML;
		/*
		 * Pass all out tags {{ }} First
		 *
		 * TODO we need to split this so only loop etc is done first, then pass to templateParse then parse
		 * out {{eval}} when the command queues are gone to prevent executing too early
		 */
		let parsedTemplate = self.templateVars(templateHTML);
		/*
		 * now pass to the templateParse to build our commands
		 */
		parsedTemplate = self.templateParse(parsedTemplate, commands);

		if (targetId === "return")
			return parsedTemplate;


		if (targetId !== false) {
			targetDom = self.getElement(targetId);
			if (!targetDom) {
				self.reportError('No valid target', 'I have no valid target to render the template [' + templateId + '] to, check the targetId [' + targetId + ']');
				return false;
			}
			self.renderToDom(targetDom, parsedTemplate, mode);
		}

		self.commandsBind(commands);

		return true;
	}

	/**
	 * Process the template looking for {{}} instances
	 * @param template
	 * @return {*}
	 */
	templateVars(template) {
		let match;
		let self = this;
		/*
		 * Fix any multi level loop references
		 */

		for (let i in self.activeLoops) {
			let loopRegex = new RegExp("#loop" + i, "g");
			let incrementRegex = new RegExp("#increment" + i, "g");
			template = template.replace(loopRegex, memory['for' + i].value.index);
			template = template.replace(incrementRegex, memory['for' + i].value.increment);
		}


		/*
		 * Look for {{#for}} loops and execute them
		 */
		const forRegex = /{{#([0-9]{0,1})for (.*?)}}([\s\S]*?){{\/for}}/;
		while (match = forRegex.exec(template)) {
			let subTemplate = '';
			match[1] = match[1] || 0;
			self.activeLoops.push(match[1]);
			/*
			 * loop through making sub templates as we go
			 *
			 * NOTE: you will notice that all index methods use 0 at the end. This is to allow
			 * for the future when we implement for loops in for loops.
			 */
			let increment = 0;
			for (let i in eval(match[2])) {
				/*
				 * Set a memory 'for0' containing the index. This is an object as in the future it
				 * may be expanded to contain other info.
				 */
				this.setMemory("for" + match[1], {"index": i, "increment": increment}, "session");
				/*
				 * This is the quick way to reference in the index using #loop[n]
				 */
				let loopRegex = new RegExp("#loop" + match[1], "g");
				let incrementMatch = match[3].replace(loopRegex, i);
				/*
				 * Process the template
				 */
				subTemplate += self.templateVars(incrementMatch, i);
				increment++;
			}
			template = template.replace(match[0], subTemplate);
			self.activeLoops.shift();
		}
		/*
	 	 * Process {{#if}}
	 	*/
		const ifRegex = /{{#if (.*?)}}([\s\S]*?){{\/if}}/;
		while (match = ifRegex.exec(template)) {
			const elseRegex = /{{#if .*?}}([\s\S]*?){{else}}([\s\S]*?){{\/if}}/g;
			let ifResult = match[2];
			let elseResult = '';
			let elseMatch = elseRegex.exec(match[0]);
			if (elseMatch) {
				ifResult = elseMatch[1];
				elseResult = elseMatch[2];
			}
			try {
				if (eval(match[1]))
					template = template.replace(match[0], self.templateVars(ifResult));
				else
					template = template.replace(match[0], self.templateVars(elseResult));
			} catch (e) {
				consoleBadge.log({
					mode: 'shields.io',
					leftText: 'Failed to eval',
					rightText: match[1],
					rightBgColor: '#ff4d4d',
					rightTextColor: '#1a1a1a'
				});
				template = template.replace(match[0], '');
			}
		}

		/*
		 * Look for any includes to directly inject templates
		 */
		const includeRegex = /{{#include (.*?)}}/;
		while (match = includeRegex.exec(template)) {
			template = template.replace(match[0], self.templateVars(self.getElement(eval(match[1])).innerHTML));
		}

		/*
		 * Process any other {{}} tags but not if they have {{!}} as those are done on command exec time
		 */
		const commandRegex = /{{([^!|~](.|\n)*?)}}/;
		while (match = commandRegex.exec(template)) {
			if (match[1][0] === '^')
				template = template.replace('"' + match[0] + '"', self.varsParser(match[1].substring(1, match[1].length)));
			else
				template = template.replace(match[0], self.varsParser(match[1]));
		}
		return template;
	}

	/**
	 * Process a json object and replace {{!}} tags + # tags
	 * @param json
	 * @return {any}
	 */
	jsonVars(json) {
		let self = this;
		json = JSON.stringify(json);
		let match;

		/*
		 * Specials #pid
		 */

		const pidRegex = /\#pid/;
		while (match = pidRegex.exec(json)) {
			json = json.replace(match[0], self.runningPid);
		}

		/*
		 * Specials #stack
		 */

		const stackRegex = /\#stack/;
		while (match = stackRegex.exec(json)) {
			json = json.replace(match[0], `queue.queue[${self.runningPid}].stack`);
		}

		/*
		 * {{!}} tags
		 */

		const commandRegex = /{{(![\^]{0,1})(.*?)}}/;
		while (match = commandRegex.exec(json)) {
			if (match[1] === '!^')
				json = json.replace('"' + match[0] + '"', self.varsParser(match[2]));
			else
				json = json.replace(match[0], self.varsParser(match[2]));
		}
		let jsonReturn = {};
		try {
			jsonReturn = JSON.parse(json);
		} catch (e) {
			self.reportError("Can not parse JSON [" + json + "]", "This error is probably fatal, check your templates");

		}
		return jsonReturn;
	}

	/**
	 * parse a var string
	 *
	 * TODO This is massively insecure. If as user can input {{}} into a form and have it displayed
	 * to other users they can take over. We either clean all input as you would with <script> etc tags or we
	 * manually write a parser.
	 *
	 * @param parseString
	 * @return {any}
	 */
	varsParser(parseString) {
		let ret = undefined;
		try {
			ret = eval(parseString);
		} catch (e) {
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Cant resolve',
				rightText: parseString,
				rightBgColor: '#ff4d4d',
				rightTextColor: '#1a1a1a'
			});
		}
		return ret;
	}

	/**
	 * Takes a template and runs any template commands contained in it to create a HTML template
	 * ready to be put into the dom
	 *
	 * @param template {string}
	 * @return {string}
	 */
	templateParse(template, commands) {
		let commandRegex = /[@\-]([a-zA-Z0-9]*?\.[a-zA-Z0-9]*?\((.|\n)*?(\);))/;
		let match = undefined;
		let parentCommand;
		let isParent;
		let self = this;
		/*
		 *  Locate all the commands in the template and generate an array of command objects that
		 *  are linked by a reference into the template
		 */
		while (match = commandRegex.exec(template)) {
			isParent = match[0][0] === '@';
			/*
			 * Generate this command object from the extracted string
			 */
			let command = self.commandParse(match[1], isParent);

			/*
			 *  In the case of an instant or sub run we don't need to leave anything in the DOM so nuke
			 */
			if (command.options.queueRun === self.DEFINE.COMMAND_INSTANT || command.options.queueRun === self.DEFINE.COMMAND_SUB || command.options.queuePrepare || command.options.queueBindTarget) {
				template = template.replace(match[0], "");
			} else {
				template = template.replace(match[0], "data-queueable=\"CMD" + command.ucid + "\"");
			}
			/*
			 *  Is this a @parent or a -child?
			 */
			if (isParent) {
				// Set the parent point to current position
				parentCommand = commands.length;

				commands.push(command);
			} else {
				// If the parent has just been created it won't have child structure
				if (commands[parentCommand].commands === undefined) {
					commands[parentCommand].commands = [];
				}
				// Put the command in the parents
				commands[parentCommand].commands.push(command);
			}
		}
		// Add the instants to the active queue
		self.commandsQueue(commands);
		return template;
	}

	/**
	 * Bind the events to the dom based on the command Object
	 * @param commandObj
	 */
	commandsBind(commandObj) {
		let self = this;
		for (let command in commandObj) {
			/*
			 * Bind queue elements will not me marked to run instantly so we pick those
			 */
			if (commandObj[command].options.queueRun !== self.DEFINE.COMMAND_INSTANT) {
				/*
				 * Find its dom entry using the selector we added
				 */
				let element;
				if (commandObj[command].options.queueBindTarget)
					element = self.getElement(commandObj[command].options.queueBindTarget);
				else
					element = self.getElement("[data-queueable=CMD" + commandObj[command].ucid + "]", false);
				if (element !== false) {
					/*
					 * Add the event. We flip it over to an instant event now because we want
					 * it triggered.
					 *
					 * The user may have specified an event and if so it will be a comma separated list
					 */
					let event = commandObj[command].options.queueEvent || "click";
					let events = event.split(",");

					for (let e in events) {
						if (events[e] === 'keydown') {
							element.addEventListener(events[e], function (e) {
								let codes = [self.DEFINE.KEY_RETURN];
								if (commandObj[command].options.queueEventCodes)
									codes = commandObj[command].options.queueEventCodes;
								if (codes.indexOf(e.keyCode) !== -1) {
									commandObj[command].options.queueRun = self.DEFINE.COMMAND_INSTANT;
									self.commandsQueue.apply(self, [[commandObj[command]]]);
								}
							});
						} else {
							element.addEventListener(events[e], function (e) {
								e.stopPropagation();
								e.preventDefault();
								commandObj[command].options.queueRun = self.DEFINE.COMMAND_INSTANT;
								self.commandsQueue.apply(self, [[commandObj[command]]]);
							});
						}
					}
				}
			}
		}

	}

	/**
	 * Take the commands array with command objects in it and add them to the queue *if* they are
	 * marked as instant. IE ready to execute
	 *
	 * @param commandObj
	 */
	commandsQueue(commandObj) {
		let self = this;
		for (let command in commandObj) {
			/*
			 * Init the stack
			 */
			commandObj[command].stack = {};
			/*
			 * DEFINE.COMMAND_INSTANT, basically a queue item we need to get running
			 */
			if (commandObj[command].options.queueRun === self.DEFINE.COMMAND_INSTANT) {
				self.queue[self.pid] = self.deepCopy(commandObj[command]);
				self.pid++;
			}
			/*
			 * Is the a prepare queue that will be triggered at some later stage
			 */
			if (commandObj[command].options.queuePrepare !== undefined) {
				self.prepare[commandObj[command].options.queuePrepare] = self.deepCopy(commandObj[command]);
				if (commandObj[command].options.queueRun == self.DEFINE.COMMAND_INSTANT) {
					consoleBadge.log({
						mode: 'shields.io',
						leftText: 'Running Prepared Queue',
						rightText: commandObj[command].options.queuePrepare,
						rightBgColor: '#aeff79',
						rightTextColor: '#1a1a1a'
					});
				} else {
					consoleBadge.log({
						mode: 'shields.io',
						leftText: 'Added Prepared Queue',
						rightText: commandObj[command].options.queuePrepare,
						rightBgColor: '#ffef6c',
						rightTextColor: '#1a1a1a'
					});
				}

			}
		}
		/*
		 *  Trigger a queue process
		 */
		self.queueProcess();
	}

	/**
	 * Execute a queue that is loaded into prepare
	 *
	 * @param prepareName {string} Name of the prepared queue
	 * @param json {object}
	 * @param silentFail {boolean}
	 */
	execute(prepareName, json, silentFail) {
		let self = this;
		if (self.prepare[prepareName] !== undefined) {
			/*
			 * Take a copy of the prepared command as we need to alter it
			 * and possibly pass new params then add it to the queue
			 */
			let dereferenceCommand = self.deepCopy(self.prepare[prepareName]);
			dereferenceCommand.options.queueRun = self.DEFINE.COMMAND_INSTANT;
			if (json !== undefined)
				dereferenceCommand.json = Object.assign(dereferenceCommand.json, json);
			self.commandsQueue.apply(self, [[dereferenceCommand]]);
			return true;
		} else {
			if (silentFail !== true)
				self.reportError("Can not execute prepare [" + prepareName + "]", "The prepared queue you requested does not exist");
			return false;
		}

	}

	/**
	 * Force a queue processing
	 *
	 * This launches the actual objects using a timeout
	 *
	 * @param sync {boolean} - Send true to force sync mode (Really only for test mode)
	 */
	queueProcess(sync) {
		let self = this;
		/*
		 *  TODO Only implementing basic queue here for testing. Concepts of active componets etc need importing
		 *  for moho
		 */
		for (let item in self.queue) {
			/*
			 *  Look for items that are QUEUE_ADDED as they need processing
			 *
			 */
			if (self.queue[item].state === self.DEFINE.QUEUE_ADDED) {
				/*
				 * Does this queueable exist?
				 */
				if (self.queueables[self.queue[item].queueable]) {

					/*
					 * Check if we have any registers that need setting
					 */

					if (!self.queue[item].options.queueRegister || (self.queue[item].options.queueRegister && self.registers.indexOf(self.queue[item].options.queueRegister) !== -1)) {

						/*
                         * Is it online? If not we fail silently as it may come online later
                         */
						if (self.queueables[self.queue[item].queueable].ready) {
							/*
                             * Update our state to be running
                             */
							self.queue[item].state = self.DEFINE.QUEUE_RUNNING;
							/*
                             * Assign a pid
                             */
							if (self.queue[item].pid === undefined) {
								self.queue[item].pid = item;
							}
							/*
                             * Check if any specific timing is needed
                             */
							self.queue[item].options.queueTimer = self.queue[item].options.queueTimer || self.defaultTimer;

							/*
                             *  Launch the function as a time out (so we get control back)
                             */

							if (sync) {
								self.runningPid = item;
								self.queueables[self.queue[item].queueable].start.apply(self.queueables[self.queue[item].queueable], [self.queue[item].pid, self.queue[item].command, self.jsonVars(self.queue[item].json), self]);
							} else {
								setTimeout(function () {
									self.runningPid = item;
									self.queueables[self.queue[item].queueable].start.apply(self.queueables[self.queue[item].queueable], [self.queue[item].pid, self.queue[item].command, self.jsonVars(self.queue[item].json), self]);
								}, self.queue[item].options.queueTimer);
							}
						}
					}
				} else {
					self.reportError("Can not find queueable [" + self.queue[item].queueable + "]", "Have you added it to the build?");
				}
			}
		}
	}

	/**
	 * Find a queue item by searching for its PID
	 * @param pid
	 * @return {*}
	 */
	findQueueByPid(pid) {
		let self = this;
		for (let item in self.queue) {
			if (self.queue[item].pid === pid) {
				return self.queue[item];
			}
		}
		return false;
	}

	/**
	 * Called by queueables to add something to our memory stack
	 * @param name
	 * @param value
	 * @param pid
	 */
	memory(pid, value) {
		let self = this;
		let command = this.findQueueByPid(pid);
		if (command) {
			let origin = command.options.memoryName || command.queueable + '.' + command.command;
			let mode = self.DEFINE.MEMORY_GARBAGE;
			if (command.options.memoryMode)
				mode = command.options.memoryMode;
			let memoryDetails = new Memory(pid, mode, origin, value);
			window.memory[origin] = memoryDetails;
			return true;
		} else {
			this.reportError("Could not set memory", "The memory set for pid [" + pid + "] could not be found");
			return false;
		}
	}

	/**
	 * Set a queue stack item
	 * @param pid
	 * @param name
	 * @param value
	 * @return {boolean}
	 */
	setStack(pid, name, value) {
		let command = this.findQueueByPid(pid);
		command.stack[name] = value;
		return true;
	}

	/**
	 * Set memory that is not associated with a running queueable (IE from the templates)
	 * @param name
	 * @param value
	 * @param mode
	 * @return {boolean}
	 */
	setMemory(name, value, mode) {
		let self = this;
		mode = mode || self.DEFINE.MEMORY_GARBAGE;
		let memoryDetails = new Memory(-1, mode, 'User', value);
		window.memory[name] = memoryDetails;
		// Are we updating perms? If so we need to sync them
		if (mode == self.DEFINE.MEMORY_PERMANENT)
			self._updateMemoryPerms();
		return true;
	}

	/**
	 * Set a register
	 * @param name
	 * @returns {boolean}
	 */
	setRegister(name) {
		if (this.registers.indexOf(name) === -1) {
			this.registers.push(name);
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Register set',
				rightText: name,
				rightBgColor: '#69ddff',
				rightTextColor: '#1a1a1a'
			});
		} else {
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Register already set',
				rightText: name,
				rightBgColor: '#69ddff',
				rightTextColor: '#1a1a1a'
			});
		}
		this.queueProcess();
		return true;
	}

	/**
	 * delete a register
	 * @param name
	 * @returns {boolean}
	 */
	deleteRegister(name) {
		if (this.registers.indexOf(name) !== -1) {
			this.registers.splice(this.registers.indexOf(name), 1);
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Register deleted',
				rightText: name,
				rightBgColor: '#ffef6c',
				rightTextColor: '#1a1a1a'
			});
		} else {
			consoleBadge.log({
				mode: 'shields.io',
				leftText: 'Register does not exist for delete',
				rightText: name,
				rightBgColor: '#ff313e',
				rightTextColor: '#1a1a1a'
			});
		}
		this.queueProcess();
		return true;
	}

	/**
	 * Delete Memory TODO clean up perms
	 * @param name
	 * @return {boolean}
	 */
	deleteMemory(name) {
		delete window.memory[name];
		this._updateMemoryPerms();
		return true;
	}

	/**
	 * Flush any permanent memory to cookies
	 * @private
	 */
	_updateMemoryPerms() {
		let self = this;
		let perms = [];
		let date = new Date();
		date.setTime(date.getTime() + (7 * 24 * 60 * 60 * 1000));
		let expires = "; expires=" + date.toUTCString();
		for (let i in window.memory) {
			if (window.memory[i].mode === self.DEFINE.MEMORY_PERMANENT) {
				self.setCookie('OT_' + i, window.memory[i]._store());
				perms.push(i);
			}
		}
		self.setCookie('OT_INDEX', window.btoa(JSON.stringify(perms)));
	}

	/**
	 * Load perm memory items from cookies
	 * @private
	 */
	_loadMemoryPerms() {
		let self = this;
		let index = self.getCookie("OT_INDEX");
		if (index !== null) {
			try {
				index = JSON.parse(window.atob(index));
				for (let i in index) {
					let perm = JSON.parse(window.atob(self.getCookie("OT_" + index[i])));
					window.memory[index[i]] = new Memory(perm.pid, perm.mode, perm.origin, perm.value);
				}
			} catch (e) {
				console.error('OT_INDEX seems corrupted');
			}
		}

	}

	/**
	 * Retrieve a cookie by name
	 * @param name - Cookie name
	 * @returns {*}
	 */
	getCookie(name) {
		let nameEQ = name + "=";
		let ca = document.cookie.split(';');
		for (let i = 0; i < ca.length; i++) {
			let c = ca[i];
			while (c.charAt(0) == ' ') c = c.substring(1, c.length);
			if (c.indexOf(nameEQ) == 0) return decodeURIComponent(c.substring(nameEQ.length, c.length));
		}
		return null;
	}

	/**
	 * set a cookie by name and value
	 * @param name - Cookie name
	 * @param value - Value of cookie
	 * @returns {*}
	 */
	setCookie(name, value) {
		let date = new Date();
		date.setTime(date.getTime() + (7 * 24 * 60 * 60 * 1000));
		const secure = window.location.href.match(/https\:\/\//i);
		const cookieString = `${name}=${value};expires=${date.toUTCString()} path=/;${(secure !== null ? 'Secure;' : '')} SameSite=Strict`;
		document.cookie = cookieString;
	}

	/**
	 * Called at the end of a queue run to flush any garbage
	 * @param pid
	 */
	cleanMemory(pid) {
		let self = this;
		for (let i in window.memory) {
			if (window.memory[i].pid === pid && window.memory[i].mode === self.DEFINE.MEMORY_GARBAGE) {
				window.memory[i] = {};
				delete window.memory[i];
			}
		}

	}


	/**
	 * Is there any work to do in the queue?
	 */
	isWork() {
		let self = this;
		let count = 0;
		for (let item in self.queue) {
			if (self.queue[item].state !== self.DEFINE.QUEUE_FINISHED && self.queue[item].state !== self.DEFINE.QUEUE_ERROR)
				count++;
		}
		return count;
	}

	/**
	 *  Clean up any finished queues
	 */
	cleanQueue() {
		for (let item in this.queue) {
			if (this.queue[item].state === this.DEFINE.QUEUE_FINISHED) {
				delete this.queue[item];
			}
		}
	}

	/**
	 *  Called to flag a queue item as finished
	 *
	 *  Normally hooked down from queueable this is a queue item saying I have finished in mode (see define.js)
	 *
	 * @param pid
	 * @param mode
	 */
	finished(pid, mode, error) {
		let self = this;

		self.queue[pid].error = error;
		if (self.queue[pid].state === self.DEFINE.QUEUE_RUNNING) {
			/*
			 * Did the command return an error? If so we will stop this queue from further execution
			 */
			if (mode == self.DEFINE.FIN_ERROR) {
				self.queue[pid].state = self.DEFINE.QUEUE_ERROR;
				self.reportError(error, 'The queueable [' + pid + '] has errored, queue put on hold');
				return;
			}
			/*
			 * Was there a warning?. This isn't serious so we just mention it to the console
			 */
			if (mode == self.DEFINE.FIN_WARNING) {
				console.log('Warning: ' + error);
			}
			/*
			 *
			 * Check if this queue has commands left
			 */
			if (self.queue[pid].commands !== undefined && self.queue[pid].commands.length > 0) {
				/*
				 * Move the next item in the queue down
				 */
				self.queue[pid].command = self.queue[pid].commands[0].command;
				self.queue[pid].queueable = self.queue[pid].commands[0].queueable;
				self.queue[pid].json = self.queue[pid].commands[0].json;
				self.queue[pid].options = self.queue[pid].commands[0].options;
				self.queue[pid].commands.shift();
				/*
				 *  Update the pid
				 *  TODO remove this as queues need to maintain their Pid for memory
				 */
				//self.queue[item].pid=self.pid;
				//self.pid++;
				self.queue[pid].state = self.DEFINE.QUEUE_ADDED;
				/*
				 * Start the queue processor as we just posted a new command
				 */
				self.queueProcess();
			} else {
				self.queue[pid].state = self.DEFINE.QUEUE_FINISHED;
				self.cleanMemory(self.queue[pid].pid);
				self.cleanQueue();
			}
			return;
		} else {
			self.reportError('Cant stop an already stopped process [' + pid + ']', 'Queue is corrupted');
			return;
		}

	}

	/**
	 * This will take a command string in the format object.command({},{}); and split it down
	 * into it parts as an object
	 *
	 * TODO no concept of the context of the command IE was it from inside a div that need binding?
	 * @param command {string}
	 * @return {object}
	 */
	commandParse(command, isParent) {
		let self = this;
		let commandObject = {"ucid": ++self.ucid};
		// Find the actual command
		let commandArray = command.match(/(.*?)\(/)[1].split('.');
		commandObject.queueable = commandArray[0];
		commandObject.command = commandArray[1];
		// Strip as we go to make follow up regex easier
		command = command.replace(/.*?\(/, '[');
		// Find first json arg

		command = command.replace(/\);$/m, ']');
		let jsonArray = JSON.parse(command);
		if (jsonArray[0]) {
			commandObject.json = jsonArray[0];
		} else {
			commandObject.json = {};
		}

		if (jsonArray[1]) {
			commandObject.options = jsonArray[1];
		} else {
			commandObject.options = {};
		}
		/*
		 * Set our default options if they haven't been set
		 *
		 * We must always have a queueRun object if its not set (normally by instant) then its either an event in
		 * which case it must be a parent or failing then its a sub
		 *
 		 */
		commandObject.options.queueRun = commandObject.options.queueRun || (isParent ? self.DEFINE.COMMAND_EVENT : self.DEFINE.COMMAND_SUB);
		commandObject.state = self.DEFINE.QUEUE_ADDED;
		return commandObject;
	}

	/**
	 * Render some text/html to the dom
	 * @param domObject {object} - The object in the dom to write to
	 * @param text {string} - The text/HTML to write
	 * @param mode {number} - Mode to use while writing see define.js
	 * @return {boolean}
	 */
	renderToDom(domObject, text, mode) {
		let self = this;
		mode = mode || self.DEFINE.RENDER_INSERT;
		switch (mode) {
			case self.DEFINE.RENDER_INSERT:
				domObject.innerHTML = text;
				break;
			case self.DEFINE.RENDER_APPEND:
				domObject.insertAdjacentHTML('beforeend', text);
				break;
			case self.DEFINE.RENDER_REPLACE:
				const html = new DOMParser().parseFromString(text, 'text/html');
				domObject.parentNode.replaceChild(html.body.firstChild, domObject);
				break;
		}
		return true;
	}

	/**
	 * Finds elements in the dom of an iframe (or current document) using the query selector
	 * @param iframeTarget Iframe or false
	 * @param elementTarget query
	 * @param errorTrap {boolean} Trap any errors?
	 * @return {object|false}
	 */
	getIframeElements(iframeTarget, elementTarget, errorTrap = true) {
		let self = this;
		let iframe = document.getElementById(iframeTarget);
		if (!iframe)
			iframe = document;
		else
			iframe = iframe.contentDocument || iframe.contentWindow.document;
		let element = iframe.querySelectorAll(elementTarget);
		/*
		 * IE11 BUG, check for non arrays and attempt to convert
		 */
		if (!Array.isArray(element)) {
			element = Array.from(element);
		}
		if (element !== null)
			return element;
		if (errorTrap)
			self.reportError('Dom Element find failed for [' + elementTarget + '] iframe [' + iframeTarget + ']', 'Follow up calls that rely on this will fail');
		return false;
	}

	/**
	 * Finds an element in the dom using the jquery formant IE #id .class tag (will only ever return one)
	 * @param elementTarget
	 * @param errorTrap {boolean} Trap any errors?
	 * @return {object|false}
	 */
	getElement(elementTarget, errorTrap = true) {
		let self = this;
		let element = document.querySelector(elementTarget);
		if (element !== null)
			return element;
		if (errorTrap)
			self.reportError('Dom Element find failed for [' + elementTarget + ']', 'Follow up calls that rely on this will fail');
		return false;
	}

	/**
	 * Finds an element(s) in the dom using the jquery formant IE #id .class tag (can return one or more)
	 * @param elementTarget
	 * @return {object|false}
	 */
	getElements(elementTarget) {
		let self = this;
		let element = document.querySelectorAll(elementTarget);
		/*
		 * IE11 BUG, check for non arrays and attempt to convert
		 */
		if (!Array.isArray(element)) {
			element = Array.from(element);
		}
		if (element !== null)
			return element;
		self.reportError('Dom Element(s) find failed for [' + elementTarget + ']', 'Follow up calls that rely on this will fail');
		return false;
	}


	/**
	 *  Show current queue status in the console DEBUG function
	 */
	show() {
		let self = this;
		if(self.queue.length>0) {
			for (let i in self.queue) {
				let indent = 0;
				self.prettyCommandObject(self.queue[i], indent);
				for (let j in self.queue[i].commands) {
					indent++;
					self.prettyCommandObject(self.queue[i].commands[j], indent);

				}
			}
		} else {
			console.log(`Queue is empty, pid is ${self.pid}`);
		}
	}

	consoleBadge(options) {
		consoleBadge.log(options);
	}

	/**
	 * Make a pretty version of the currrent commandObject and dump it to the console
	 * @param commandObject
	 * @param indent
	 */
	prettyCommandObject(commandObject, indent) {
		let self = this;
		let string = '';
		for (var i = 0; i < indent; i++) {
			string += ' ';
		}
		let color = self.DEFINE.CONSOLE_COL_GREEN;
		switch (commandObject.state) {
			case self.DEFINE.QUEUE_FINISHED:
				color = self.DEFINE.CONSOLE_COL_AMBER;
				break;
			case self.DEFINE.QUEUE_ERROR:
				color = self.DEFINE.CONSOLE_COL_RED;
				break;

		}
		string += commandObject.queueable + '.' + commandObject.command + '(' + JSON.stringify(commandObject.json) + ',' + JSON.stringify(commandObject.options) + ');'
		console.log('%c ' + string, color);
		if (commandObject.error)
			console.log('%c  Stopped: ' + commandObject.error, self.DEFINE.CONSOLE_COL_AMBER);
	}

	/**
	 * Report an error to the console, adds various internal stats
	 * @param error
	 * @param message
	 */
	reportError(error, message) {
		consoleBadge.log({
			mode: 'shields.io',
			leftText: error,
			rightText: message,
			rightBgColor: '#ff0707',
			rightTextColor: '#1a1a1a'
		});
	}

	/**
	 * Deep copy and object IE remove references
	 * @param inputObject
	 * @return {any}
	 */
	deepCopy(inputObject) {
		return JSON.parse(JSON.stringify(inputObject));
	}

	/**
	 * Map an object with sub objects using a map function
	 * @param obj
	 * @param mapFunction
	 */
	objectMap(obj, mapFunction) {
		for (let i in obj) {
			if (typeof obj[i] === 'object') {
				this.objectMap(obj[i], mapFunction);
			} else {
				obj[i] = mapFunction(obj[i]);
			}
		}
	}

	/**
	 * Adds classes for browser type to body for use in CSS
	 */
	browserClasses() {
		let self = this;
		let bodyElement = self.getElement("body");
		if (!!window.MSInputMethodContext && !!document.documentMode)
			bodyElement.classList.add("ie11");
		else
			bodyElement.classList.add("notie11");
		if (navigator.vendor.match(/apple/i))
			bodyElement.classList.add("safari");
		if (navigator.vendor.match(/google/i))
			bodyElement.classList.add("chrome");
		if (navigator.userAgent.indexOf("Edge") > -1)
			bodyElement.classList.add("edge");
		if (navigator.userAgent.toLowerCase().indexOf('firefox') > -1)
			bodyElement.classList.add("firefox");
	}

	/**
	 * Get any params from the url in json format
	 */
	urlToJson() {
		let url = location.search;
		let query = url.substr(1);
		let result = {};
		query.split("&").forEach(function (part) {
			let item = part.split("=");
			result[item[0]] = decodeURIComponent(item[1]);
		});
		return result;
	}

	/**
	 *  Work in progress,
	 */
	menu() {
		queue.setMemory('developer', !this.developerMode, "Permanent");
		alert('DEVELOPER MODE: ' + this.developerMode)
		//document.body.innerHTML+='<div id="ourthingsMenu"><button onclick="queue.toggleDebug()">DEBUG MODE</button></div>';
	}

}

export default Queue;

/** @module Api */
import Queueable from "../Queueable";
import {v4 as uuidv4} from 'uuid';

/**
 * @classdesc
 *
 * API connection methods
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 */

const MAX_BYTES = 50000;

export default class Api extends Queueable {

	/**
	 * Make a GET request
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.url - URL to make GET request to
	 * @param {string} json.contentType=application/json - Content type to request
	 * @param {string} json.header - header object to send (note Content-Type is overwritten by above setting)
	 * @param {string} json.name - name of error queue to use
	 */
	get(pid, json) {
		let self = this;
		json.contentType = json.contentType || 'application/json';
		json.name = json.name || 'apiError';
		let headers = json.headers || {};
		headers['Content-Type'] = json.contentType || 'application/json';
		fetch(json.url, {
			headers: headers,
		})
			.then(function (response) {
				if (!response.ok) {
					self.queue.setMemory('apiErrorDetail', {
						"json": json,
						"error": response
					}, self.queue.DEFINE.MEMORY_SESSION);
					self.queue.execute(json.name);
				}
				self.queue.handleFetchErrors(response);
				return response;
			})
			.then(function (response) {
				switch (json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid, response);
				self.finished(pid, self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				self.queue.setMemory('apiErrorDetail', {
					"json": json,
					"error": error
				}, self.queue.DEFINE.MEMORY_SESSION);
				self.queue.execute(json.name);
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("api.get Warning this error is probably fatal");
			});

	}

	/**
	 * Make a POST request
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.url - URL to make GET request to
	 * @param {string} json.contentType=application/json - Content type to request
	 * @param {string} json.header - header object to send (note Content-Type is overwritten by above setting)
	 * @param {string} json.body - object to send JSON.stringify is applies to this
	 * @param {string} json.name - name of error queue to use
	 */
	post(pid, json) {
		let self = this;
		json.contentType = json.contentType || 'application/json';
		json.name = json.name || 'apiError';
		let headers = json.headers || {};
		headers['Content-Type'] = json.contentType || 'application/json';
		fetch(json.url, {
			headers: headers,
			method: 'POST',
			body: JSON.stringify(json.body)
		})
			.then(function (response) {
				if (!response.ok) {
					self.queue.setMemory('apiErrorDetail', {
						"json": json,
						"error": response
					}, self.queue.DEFINE.MEMORY_SESSION);
					self.queue.execute(json.name);
				}
				self.queue.handleFetchErrors(response);
				return response;
			})
			.then(function (response) {
				switch (json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid, response);
				self.finished(pid, self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				self.queue.setMemory('apiErrorDetail', {
					"json": json,
					"error": error
				}, self.queue.DEFINE.MEMORY_SESSION);
				self.queue.execute(json.name);
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("api.post Warning this error is probably fatal");
			});

	}

	/**
	 * Make a PUT request
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.url - URL to make PUT request to
	 * @param {string} json.contentType=application/json - Content type to request
	 * @param {string} json.header - header object to send (note Content-Type is overwritten by above setting)
	 * @param {string} json.body - object to send JSON.stringify is applies to this
	 * @param {string} json.name - name of error queue to use
	 */
	put(pid, json) {
		let self = this;
		json.contentType = json.contentType || 'image/png';
		json.name = json.name || 'apiError';
		let headers = json.headers || {};
		headers['Content-Type'] = json.contentType;
		fetch(json.url, {
			headers: headers,
			method: 'PUT',
			body: window.atob(json.body)
		})
			.then(function (response) {
				if (!response.ok) {
					self.queue.setMemory('apiErrorDetail', {
						"json": json,
						"error": response
					}, self.queue.DEFINE.MEMORY_SESSION);
					self.queue.execute(json.name);
				}
				self.queue.handleFetchErrors(response);
				return response;
			})
			.then(function (response) {
				switch (json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid, response);
				self.finished(pid, self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				self.queue.setMemory('apiErrorDetail', {
					"json": json,
					"error": error
				}, self.queue.DEFINE.MEMORY_SESSION);
				self.queue.execute(json.name);
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("api.put Warning this error is probably fatal");
			});

	}

	/**
	 * Create a new websocket
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.url - URL to connect websocket too
	 * @param {string} json.action - What json param will contain the 'action' router
	 * @param {string} json.queues - Array of {action:"action", queue:"queue" }

	 */
	websocketInit(pid, json) {
		let self = this;
		let options = Object.assign({
			"url": "ws://localhost",
			"queue": "queue",
			"queues": {},
			"recvQeue": false
		}, json);
		self.frames = self.frames || {};
		self.bulk = self.bulk || [];
		self.bulkQueue = 'bulkQueue';
		self.socket = new WebSocket(options.url);
		self.socket.onopen = function (event) {
			self.finished(pid, self.queue.DEFINE.FIN_OK);
		};
		self.socket.onmessage = function (event) {
			let stack = [];
			let jsonData = JSON.parse(event.data);

			/*
			 * Is this part of a multi packet?
			 *
			 * For AWS websockets size is limited so we split packets down into frames IE:
			 *
			 * { frame: 1, totalFrames: 10, data: "BASE64" }
			 *
			 * This decodes those frames, you will need to implement the split in your AWS websocket code
			 */
			if (jsonData['frame']!==undefined) {
				//console.log(`${jsonData['uuid']} - ${jsonData['frame']} of ${jsonData['totalFrames']}`);
				if (self.frames[jsonData['uuid']] === undefined) {
					self.frames[jsonData['uuid']] = {"total": 0, data: new Array(parseInt(jsonData['totalFrames']))};
				}
				if(!self.frames[jsonData['uuid']].data[parseInt(jsonData['frame']) - 1]) {
					self.frames[jsonData['uuid']].data[parseInt(jsonData['frame']) - 1] = atob(jsonData['data']);
					self.frames[jsonData['uuid']].total++;
				} else {
					console.log(`Duplicate network packet!!! ${jsonData['uuid']} - ${jsonData['frame']}`);
				}
				if (self.frames[jsonData['uuid']].total === jsonData['totalFrames']) {
					const realJsonData = JSON.parse(self.frames[jsonData['uuid']].data.join(''));
					self.frames[jsonData['uuid']] = null;
					delete self.frames[jsonData['uuid']];
					jsonData = realJsonData;
					deployEvent();
				} else if (self.frames[jsonData['uuid']].total > jsonData['totalFrames']) {
					console.log('WARNING NETWORK CRAZY');
				}


			} else {
				/*
				 * Is this a super large packet using S3?
				 */
				if (jsonData['s3']) {
					fetch(jsonData['s3'], {})
						.then(function (response) {
							if (!response.ok) {
								self.queue.setMemory('wsErrorDetails', response, self.queue.DEFINE.MEMORY_SESSION);
								self.queue.execute("wsError");
							}
							self.queue.handleFetchErrors(response);
							return response;
						})
						.then(function (response) {
							return response.blob();
						})
						.then(function (response) {
							const reader = new FileReader();
							reader.addEventListener('loadend', () => {
								jsonData = JSON.parse(reader.result);
								deployEvent();
							});
							reader.readAsText(response);


						})
						.catch(function (error) {
							self.queue.setMemory('wsErrorDetails', error, self.queue.DEFINE.MEMORY_SESSION);
							self.queue.execute("wsError");

						});
				} else {
					deployEvent();
				}
			}

			function deployEvent() {
				/*
				 * We push data to the stack for anyone using stack mode
				 */
				if (memory[`wsStack_${jsonData[options.queue]}`])
					stack = memory[`wsStack_${jsonData[options.queue]}`].value;
				stack.push(jsonData);
				self.queue.setMemory(`wsStack_${jsonData[options.queue]}`, stack, self.queue.DEFINE.MEMORY_SESSION);
				/*
				 * Set our normal memory (not multiple thread safe)
				 */
				self.queue.setMemory(jsonData[options.queue], jsonData, self.queue.DEFINE.MEMORY_SESSION);
				self.queue.setMemory('wsLastRecv', jsonData, self.queue.DEFINE.MEMORY_SESSION);


				/*
				 * Do we need to trigger event? If we have bulk calls coming in then only if its the last
				 */
				let wasBulk = false;
				for (let i in self.bulk) {
					if (self.bulk[i][options.queue] === jsonData[options.queue]) {
						self.bulk.splice(i, 1);
						wasBulk = true;
					}
				}

				if (wasBulk === false) {
					self.queue.execute(jsonData[options.queue]);
					if (options.recvQeue)
						self.queue.execute(options.recvQeue);
				} else {
					if (wasBulk === true && self.bulk.length === 0) {
						self.queue.execute(self.bulkQueue);
						if (options.recvQeue)
							self.queue.execute(options.recvQeue);
					}
				}
			}

		};

		self.socket.onclose = function (event) {
			self.queue.setMemory('wsCloseDetails', event, self.queue.DEFINE.MEMORY_SESSION);
			self.queue.execute("wsClose");
		};

		self.socket.onerror = function (event) {
			self.queue.setMemory('wsErrorDetails', event, self.queue.DEFINE.MEMORY_SESSION);
			self.queue.execute("wsError");
		};
	}

	websocketPop(pid, json) {
		let options = Object.assign({
			"prefix": "ws://localhost",
			"queue": "queue",
			"queues": {}
		}, json);
		this.queue.setStack(pid, options.queue, memory[`wsStack_${json.queue}`].value.pop());
		this.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Send a json message down the websocket
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.message - JSON message to send
	 * @param {string} json.bulk - Bulk messages
	 * @param {string} json.bulk - Bulk bulkQueue
	 * @param {string} json.debug - Debug to console
	 * @param {string} json.sendQueue - Queue to always call on send
	 *
	 */
	websocketSend(pid, json) {
		let self = this;
		let options = Object.assign({
			"debug": false,
			"sendQueue": false
		}, json);
		if (options.debug === true)
			console.log(json);
		self.queue.setMemory('wsLastSent', json, self.queue.DEFINE.MEMORY_SESSION);
		if (options.sendQueue)
			self.queue.execute(options.sendQueue);
		if (json.bulk) {
			/*
			 * Bulk mode, we are sending lots of requests and return triggers only work when we get it all back
			 */
			self.bulk = json.bulk;
			self.bulkQueue = json.bulkQueue;
			for (let i in self.bulk) {
				self._websocketSendActual(self.bulk[i])
				//self.socket.send(JSON.stringify(self.bulk[i]));
			}
		} else {
			//self.socket.send(JSON.stringify(json.message));
			self._websocketSendActual(json.message);
		}
		self.finished(pid, self.queue.DEFINE.FIN_OK);
	}


	_websocketSendActual(json) {
		let self = this;
		self.currentPacket = 0;
		self.totalPackets = 0;
		self.packetArray = [];
		self.uuid = uuidv4();
		const payload = JSON.stringify(json);
		if (payload.length > MAX_BYTES) {
			self.totalPackets = Math.ceil(payload.length / MAX_BYTES);
			for (let i = 0; i < self.totalPackets; i++) {
				let loc = i * MAX_BYTES;
				let sub = payload.slice(loc, MAX_BYTES + loc);
				self.packetArray.push(sub);
			}
			self._websocketSendPacket();
		} else {
			try {
				self.socket.send(payload);
			} catch (event) {
				self.queue.setMemory('wsErrorDetails', event, self.queue.DEFINE.MEMORY_SESSION);
				self.queue.execute("wsError");

			}
		}
	}

	_websocketSendPacket() {
		let self = this;
		/*
		 * more work?
		 */
		if (self.currentPacket < self.totalPackets) {
			let packet = btoa(self.packetArray.shift());
			self.currentPacket++;
			//console.log(`packet:${self.currentPacket}-${self.totalPackets} Size: ${packet.length}`);

			try {
				self.socket.send(JSON.stringify({
					"frame": self.currentPacket,
					"totalFrames": self.totalPackets,
					"uuid": self.uuid,
					"data": packet
				}));
			} catch (event) {
				self.queue.setMemory('wsErrorDetails', event, self.queue.DEFINE.MEMORY_SESSION);
				self.queue.execute("wsError");
			}
			setTimeout(function () {
				self._websocketSendPacket();
			}, 100);
		}
	}

}

/** @module ourthings/Queueable/Browser */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Browser Functions
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 */
export default class Browser extends Queueable {


	/**
	 * Redirect browser to a new page
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.location - Dom location to direct to
	 */
	redirect(pid, json) {
		window.top.location = json.location;
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Start the history monitor.
	 *
	 * Detects when the user navigates via back / forward button and runs queue based on history item
	 * added via addHistory
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 */
	initHistory(pid, json) {
		let self = this;
		self.lastURL=false;

		function urlChange(e) {
			let baseURL = location.href;

			/*
			 * Do a check to see we are not trying to go to the same url
			 * In some browsers we will get two events (onpopstate & onhashchange)
			 */

			if(self.lastURL===false||baseURL!==self.lastURL) {
				if(memory.historyClose&&memory.historyClose.value==='on') {
					self.queue.setMemory('historyBlockedUrl', baseURL, "Session");
					location.href = self.lastURL;
					self.queue.execute('historyBlocked');
					return;
				}
				self.lastURL=baseURL;
				let match = baseURL.match(/\#([a-zA-Z]+)\/{0,1}(.*)$/);
				if (match && match[1]) {
					if (match[2])
						self.queue.setMemory('history', match[2].split('/'), "Session");
					self.queue.execute('history' + match[1]);
				} else {
					self.queue.execute('historyRoot');
				}
			}
		}

		window.onpopstate = urlChange;
		/*
		 * IE's *sigh*
		 */
		window.onhashchange = urlChange;

		if (json.checkURL) {
			let baseURL = location.href;
			let match = baseURL.match(/\#([a-zA-Z]+)\/{0,1}(.*)$/);
			if (match && match[1]) {
				if (match[2])
					self.queue.setMemory('history', match[2].split('/'), "Session");
				self.queue.execute('history' + match[1]);
				this.finished(pid, this.queue.DEFINE.FIN_OK);
				return;
			}
		}

		if(json.default) {
			self.addHistory(-1,{"history":json.default});
		}

		this.finished(pid, this.queue.DEFINE.FIN_OK);

	}

	/**
	 * Add a history entry
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.history - history name to add
	 * @param {string} json.force - force history on same url
	 */
	addHistory(pid, json) {
		let options = Object.assign({
			"history": "Root",
		}, json);
		let baseURL = location.href;

		if (baseURL.match(/#/))
			baseURL = baseURL.slice(0, location.href.indexOf('#'));
		/*
		 * Some browsers 'Looking at you chrome' register a change event even if the URL is the same
		 * so we filter any matches out
		 */
		const newURL=baseURL + '#' + options.history;
		if (newURL !== location.href||options.force==true) {
			location.href = newURL;
		}
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	backHistory(pid, json) {
		window.history.back();
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	forwardHistory(pid, json) {
		window.history.forward();
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Add a resize monitor that runs a queue on resize
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - queue to run on resize
	 */
	resizeMonitor(pid, json) {
		let self = this;
		window.onresize = function (event) {
			self.queue.execute(json.name);
		};
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Toggle a close page trap on and off
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.mode - on|off
	 */
	closeEvent(pid,json) {
		this.queue.setMemory('historyClose', json.mode? json.mode:'on', "Session");
		if(json.mode==='off') {
			window.onbeforeunload = function(e) {};
		} else {
			window.onbeforeunload = blockClose;
		}

		function blockClose(e) {
			e = e || window.event;
			// For IE and Firefox prior to version 4
			if (e) {
				e.returnValue = 'Sure?';
			}
			// For Safari
			return 'Sure?';
		}
		this.finished(pid, this.queue.DEFINE.FIN_OK);

	}

	/**
	 * Set page title
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.title - new page title
	 */
	setTitle(pid,json) {
		document.title = json.title;
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}
}

/** @module ourthings/Queueable/Elements */
import Queueable from "../Queueable";
import {Validate, ValidateEmail, ValidatePassword, ValidateText} from "../Validator";

/**
 * @classdesc
 *
 * Dom Elements manipulations
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * elements.removeClass({"targetId":".leftPanel","class":"hidden"});
 *
 */
export default class Elements extends Queueable {

	/**
	 * Add a class to a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.iframeId - iframe target
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.class - Name of class to add
	 *
	 * @example
	 * elements.addClass({"targetId":".leftPanel","class":"hidden"});
	 */
	addClass(pid, json) {
		let self = this;
		let elements = self.queue.getIframeElements(json.iframeId, json.targetId);
		self.set(pid, json);

		if (elements !== false) {
			elements.forEach(function (element) {
				element.classList.add(json.class);
			});
			self.finished(pid, self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid, self.queue.DEFINE.FIN_WARNING, 'Could not add class [' + json.class + '] to [' + json.targetId + ']');
		}
	}

	/**
	 * Set style of a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.iframeId - iframe target
	 * @param {string} json.targetId - Dom target
	 * @param {array} json.style - style array of elements in format [{"name":"background","value":"red"}]
	 *
	 * @example
	 * elements.setStyle({"targetId":".leftPanel","style":[{"name":"background","value":"red"}]});
	 */
	setStyle(pid, json) {
		let self = this;
		let elements = self.queue.getIframeElements(json.iframeId, json.targetId);
		self.set(pid, json);

		if (elements !== false) {
			elements.forEach(function (element) {
				for (let i in json.style)
					element.style[json.style[i].name] = json.style[i].value;
			});
			self.finished(pid, self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid, self.queue.DEFINE.FIN_WARNING, 'Could not set style on [' + json.targetId + ']');
		}
	}

	/**
	 * Set attribute of a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.iframeId - iframe target
	 * @param {string} json.targetId - Dom target
	 * @param {array} json.attribute - Attribute to set
	 * @param {array} json.value - Value to set
	 *
	 * @example
	 * elements.setAttribute({"targetId":".leftPanel","attribute":"src","value":"http://foo/bar.png"});
	 */
	setAttribute(pid, json) {
		let self = this;
		let elements = self.queue.getIframeElements(json.iframeId, json.targetId);
		self.set(pid, json);

		if (elements !== false) {
			elements.forEach(function (element) {
				element.setAttribute(json.attribute, json.value);
			});
			self.finished(pid, self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid, self.queue.DEFINE.FIN_WARNING, 'Could not set attribute [' + json.attribute + '] on [' + json.targetId + ']');
		}
	}

	/**
	 * Remove a class to a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.iframeId - iframe target
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.class - Name of class to remove
	 *
	 * @example
	 * elements.removeClass({"targetId":".leftPanel","class":"hidden"});
	 */
	removeClass(pid, json) {
		let self = this;
		let elements = self.queue.getIframeElements(json.iframeId, json.targetId);
		self.set(pid, json);
		if (elements !== false) {
			elements.forEach(function (element) {
				element.classList.remove(json.class);
			});
			self.finished(pid, self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid, self.queue.DEFINE.FIN_WARNING, 'Could not remove class [' + json.class + '] to [' + json.targetId + ']');
		}
	}

	/**
	 * toggle a class on a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.class - Name of class to toggle
	 *
	 * @example
	 * elements.toggleClass({"targetId":".leftPanel","class":"hidden"});
	 */
	toggleClass(pid, json) {
		let self = this;
		let elements = self.queue.getIframeElements(json.iframeId, json.targetId);
		self.set(pid, json);
		if (elements !== false) {
			elements.forEach(function (element) {
				if (element.classList.contains(json.class))
					element.classList.remove(json.class);
				else
					element.classList.add(json.class);
			});
			self.finished(pid, self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid, self.queue.DEFINE.FIN_WARNING, 'Could not remove class [' + json.class + '] to [' + json.targetId + ']');
		}
	}

	/**
	 * Cut a dom element out and paste it somewhere
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.sourceId - Source dom element
	 *
	 * @example
	 * elements.domCutPaste({"targetId":".leftPanel","sourceId":".thing"});
	 */
	domCutPaste(pid, json) {
		let source = self.queue.getElement(json.sourceId);
		let target = self.queue.getElement(json.targetId);
		target.appendChild(source);
		this.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Delete element from dom
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target to removce
	 *
	 * @example
	 * elements.domRemoveElement({"targetId":".leftPanel"});
	 */
	domRemoveElement(pid, json) {
		let target = self.queue.getElement(json.targetId);
		target.remove();
		this.finished(pid, self.queue.DEFINE.FIN_OK);
	}



	/**
	 * Set the HTML of an element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.html - HTML to add
	 * @param {string} json.append - Append mode
	 *
	 * @example
	 * elements.innerHTML({"targetId":".leftPanel","html":"Hello World"});

	 */
	innerHTML(pid, json) {
		let self = this;
		let element = self.queue.getElement(json.targetId);
		if(element) {
			if (json.append)
				element.insertAdjacentHTML('beforeend', json.html);
			else
				element.innerHTML = json.html;
		}
		self.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Set the value of an input field
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.value - value to set
	 *
	 * @example
	 * elements.setInputValue({"targetId":"#username","value":"Hello World"});

	 */
	setInputValue(pid, json) {
		let self = this;
		let element = self.queue.getElement(json.targetId);
		if(json.pre)
			json.value=json.pre+json.value;
		if(json.post)
			json.value=json.value+json.post;

		if(json.append===true)
			element.value += json.value;
		else
			element.value = json.value;

		self.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Get the values from checked input boxes
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target for check boxes
	 * @param {string} json.mode - array|string
	 * @param {string} json.separator - What seperator to use in string mode
	 * @param {string} json.name - Name of the memory element to set
	 *
	 *
	 * @example
	 * elements.getCheckBoxValues({"mode":"string","targetId":".functionGetTypes"});

	 */
	getCheckBoxValues(pid, json) {
		let self = this;
		let options = Object.assign({
			"mode": "array",
			"separator": ",",
			"name": "checkboxes"
		}, json);
		let results = [];
		let elements = self.queue.getElements(json.targetId + ':checked');
		for (let element in elements) {
			results.push(elements[element].value);
		}
		if (options.mode !== 'array') {
			results = results.join(options.separator);
		}
		self.queue.setMemory(options.name, results, "Session");
		self.finished(pid, self.queue.DEFINE.FIN_OK);

	}

	getMultiselectValues(pid,json) {
		let self = this;
		let options = Object.assign({
			"mode": "array",
			"name": "selectboxes"
		}, json);
		const results = Array.prototype.slice.call(document.querySelectorAll(`${options.targetId} option:checked`),0).map(function(v,i,a) {
			return v.value;
		});
		self.queue.setMemory(options.name, results, "Session");
		self.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Get the computed style for an element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.styles - value to get
	 * @param {string} json.name - memory name to use
	 *
	 * @example
	 * elements.getComputedStyle({"targetId":"#username","styles":"background"});

	 */
	getComputedStyle(pid, json) {
		let self = this;
		let options = Object.assign({
			"name": "computedStyle"
		}, json);
		let element = self.queue.getElement(options.targetId);
		let style = window.getComputedStyle(element).getPropertyValue(options.css);
		self.queue.setMemory(options.name, style, "Session");
		self.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Scroll to a dom target
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 *
	 * @example
	 * elements.scrollIntoView({"targetId":"#ps_1"});

	 */
	scrollIntoView(pid, json) {
		let self = this;
		let element = self.queue.getElement(json.targetId);
		element.scrollIntoView();
		self.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Scroll to bottom of a dom target
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 *
	 * @example
	 * elements.scrollToBottom({"targetId":"#ps_1"});

	 */
	scrollToBottom(pid, json) {
		let objDiv = this.queue.getElement(json.targetId);
		objDiv.scrollTop = objDiv.scrollHeight;
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Accessible toggle visible on a target
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target to scroll to*
	 * @param {string} json.mode - toggle|add|remove
	 * @example
	 * elements.focus({"targetId":"#ps_1"});
	 */
	ariaHiddenToggle(pid, json) {
		let self = this;
		let options = Object.assign({
			"class": "hidden",
			"mode": "toggle"
		}, json);
		let elements = self.queue.getIframeElements(options.iframeId, options.targetId);
		if (elements !== false) {
			elements.forEach(function (element) {
				if (options.mode === "toggle") {
					if (element.classList.contains(options.class)) {
						element.classList.remove(options.class);
						element.setAttribute('aria-hidden', 'false');
					} else {
						element.classList.add(options.class);
						element.setAttribute('aria-hidden', 'true');
					}
				} else {
					if (options.mode === "add") {
						element.setAttribute('aria-hidden', 'true');
						element.classList.add(options.class);
					}
					if (options.mode === "remove") {
						element.setAttribute('aria-hidden', 'false');
						element.classList.remove(options.class);
					}
				}
			});
			self.finished(pid, self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid, self.queue.DEFINE.FIN_WARNING, 'Could not remove class [' + options.class + '] to [' + options.targetId + ']');
		}
	}


	/**
	 * Focus on a target
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target to scroll to*
	 * @example
	 * elements.focus({"targetId":"#ps_1"});
	 */
	focus(pid, json) {
		let objDiv = this.queue.getElement(json.targetId);
		objDiv.focus();
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Scroll a dom container target to a dom target
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target to scroll to
	 * @param {string} json.containerId - Dom target container to scroll
	 *
	 * @example
	 * elements.scrollIntoView({"containerId":".map-elements-list","targetId":"#ps_1"});
	 */
	scrollContainerToElement(pid, json) {
		let self = this;
		let container = self.queue.getElement(json.containerId);
		let element = self.queue.getElement(json.targetId);
		container.scrollTop = element.offsetTop;
		self.finished(pid, self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Scroll a dom container to the top
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target to scroll to
	 *
	 * @example
	 * elements.scrollTop({"targetId":".map-elements-list"});
	 */
	scrollTop(pid, json) {
		let container = self.queue.getElement(json.targetId);
		container.scrollTop = 0;
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Monitor element(s) in a form and add classes on change
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - elements(s) to monitor for change and add modifiedClass to
	 * @param {string} json.buttonId - Element to add modifiedClass to
	 * @param {string} json.modifiedClass - Class to add to modified elements
	 * @param {string} json.modifiedQueue - Prepared queue to run when element modified
	 *
	 * @example
	 * elements.formActivityMonitor({"targetId":".functionMonitor","buttonId":".form-save","modifiedClass":"modified"});

	 */
	formActivityMonitor(pid, json) {
		let self = this;
		let elements = this.queue.getElements(json.targetId);
		let button;
		if (json.buttonId)
			button = this.queue.getElement(json.buttonId);
		elements.forEach(function (element) {
			element.addEventListener("change", function () {
				if (json.modifiedQueue)
					self.queue.execute(json.modifiedQueue, {});
				if (json.modifiedClass) {
					this.classList.add(json.modifiedClass);
					if (button)
						button.classList.add(json.modifiedClass);
				}
			});

			element.addEventListener("keypress", function () {
				if (json.modifiedQueue)
					self.queue.execute(json.modifiedQueue, {});
				if (json.modifiedClass) {
					this.classList.add(json.modifiedClass);
					if (button)
						button.classList.add(json.modifiedClass);
				}
			});

		});
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Monitor element(s) in a form and validate
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - elements(s) to monitor for change and add modifiedClass to
	 * @param {string} json.buttonId - Element to add modifiedClass to
	 * @param {string} json.focusClass - Class to add to focuses elements
	 * @param {string} json.validClass - Prepared queue to run when element modified
	 * @param {string} json.errorClass - Prepared queue to run when element modified
	 * @param {string} json.timeout - Length of time after user has finished typing to update
	 *
	 * @example
	 * elements.formValidityMonitor({"targetId":".functionMonitor","buttonId":".submit-form","validClass":"valid","focusClass":"focus","errorClass":"error"},{"queueRun":"Instant"});

	 */
	formValidityMonitor(pid, json) {
		let self = this;
		let elements = this.queue.getElements(json.targetId);
		let button;
		if (json.buttonId)
			button = this.queue.getElement(json.buttonId);
		let modules = {};
		let timerEvent;
		let timerTimeout = json.timeout || 2000;
		modules['email'] = new ValidateEmail();
		modules['text'] = new ValidateText();
		modules['password'] = new ValidatePassword();
		elements.forEach(function (element) {
			/*
			 * Focus is new, clear down focused classes and reclass
			 */
			element.addEventListener("focus", function () {
				elements.forEach(function (element) {
					element.classList.remove(json.focusClass);
				});
				this.classList.add(json.focusClass);
			});

			/*
			 * There is a change to the field (normally they exit the field
			 */
			element.addEventListener("change", function () {
				if (timerEvent)
					clearTimeout(timerEvent);
				changeUpdate(element, this);
			});

			/*
			 * Key up so after the user has finishing typing
			 */
			element.addEventListener("keyup", function (e) {
				if (e.which !== 9) {
					if (timerEvent)
						clearTimeout(timerEvent);
					let ptr = this;
					timerEvent = setTimeout(function () {
						changeUpdate(element, ptr);
					}, timerTimeout);
				}
			});

		});

		/*
		 * Function to actually update the fields
		 */
		function changeUpdate(element, ptr) {
			if (element.getAttribute('data-validation')) {
				let moduleName = element.getAttribute('data-validation').toLowerCase();
				if (modules[moduleName].valid(element.value, {})) {
					ptr.classList.remove(json.errorClass);
					ptr.classList.add(json.validClass);
				} else {
					ptr.classList.add(json.errorClass);
					ptr.classList.remove(json.validClass);
				}
				let needValidations = self.queue.getElements(json.targetId + '[data-validation]');
				let isValidated = self.queue.getElements(json.targetId + '[data-validation].' + json.validClass);
				if (needValidations.length === isValidated.length) {
					button.classList.add(json.validClass);
				} else {
					button.classList.remove(json.validClass);
				}
			}
		}

		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}
	/**
	 * dragReset - Reset the drag item
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.drag - the drag item to reset
	 *
	 * @example
	 * -elements.dragReset({"drag":"mydraf"});

	 */
	dragReset(pid, json) {
		let self = this;
		let options = Object.assign({
			"drag": "default",
			"original":false
		}, json);
		let element = this.drags[options.drag].element;
		if(options.original) {
			element.style.removeProperty('top');
			element.style.removeProperty('left');
			self.drags[options.drag].pos= {x: element.offsetLeft, y: element.offsetTop,ox:0,oy:0};
		} else {
			if (this.drags[options.drag].mode) {
				this.drags[options.drag].stored = {
					x: element.offsetLeft,
					y: element.offsetTop
				}
				element.style.removeProperty('top');
				element.style.removeProperty('left');
				element.style.removeProperty('position');
			} else {
				element.style.top = self.drags[options.drag].stored.y + "px";
				element.style.left = self.drags[options.drag].stored.x + "px";
			}
			this.drags[options.drag].mode = !this.drags[options.drag].mode;
		}
		this.finished(pid, this.queue.DEFINE.FIN_OK);

	}

	/**
	 * dragMoveIntoBounds - Force a drag back into bounds
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.drag - the drag item to reset
	 *
	 * @example
	 * -elements.dragBoundsUpdate({"drag":"mydraf"});

	 */
	dragMoveIntoBounds(pid,json) {
		let self = this;
		let options = Object.assign({
			"drag": "default",
			"original":false
		}, json);
		/*
		 * Check our X
		 */

		let element = this.drags[options.drag].element;
		self.drags[options.drag].dimensions=element.getBoundingClientRect();
		let bbox=this.drags[options.drag].boundsElement.getBoundingClientRect();
		this.drags[options.drag].boundary={
			x:0,
			y:0,
			x1:bbox.width,
			y1:bbox.height
		};
		if((self.drags[options.drag].pos.x+self.drags[options.drag].buffer+self.drags[options.drag].dimensions.width)>self.drags[options.drag].boundary.x1)
		{
			self.drags[options.drag].pos.x=self.drags[options.drag].boundary.x1-(self.drags[options.drag].buffer+self.drags[options.drag].dimensions.width);
			element.style.left = self.drags[options.drag].pos.x + "px";

		}
		this.finished(pid, this.queue.DEFINE.FIN_OK);

	}

	/**
	 * dragBoundsUpdate - Reset the boundary area manually
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.drag - the drag item to reset
	 *
	 * @example
	 * -elements.dragBoundsUpdate({"drag":"mydraf"});

	 */
	dragBoundsUpdate(pid, json) {
		let self = this;
		let options = Object.assign({
			"drag": "default"
		}, json);
		let bbox=this.drags[options.drag].boundsElement.getBoundingClientRect();
		this.drags[options.drag].boundary={
			x:0,
				y:0,
				x1:bbox.width,
				y1:bbox.height
		};
		this.finished(pid, this.queue.DEFINE.FIN_OK);

	}

	/**
	 * dragOn - Make an element dragable
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.drag - the drag item to use
	 * @param {string} json.targetId - element to be dragable
	 * @param {string} json.dragTargetId - element to be the area you can actually drag
	 * @param {string} json.bounds - element to be the area you cant drag out of
	 * @param {int} json.buffer - the amount of buffer in pixels to apply to the boundary
	 *
	 * @example
	 * -elements.dragOn({"targetId":"#functionDragAnalyticsTop","dragTargetId":"#functionDragAnalyticsTop .dragbar","drag":"analytics","bounds":".edit-scenario-map"});
	 */
	dragOn(pid, json) {
		let self = this;
		let options = Object.assign({
			"drag": "default",
			"buffer": 10
		}, json);

		if (this.drags === undefined) {
			this.drags = {};
		}

		// The thing me move
		let element = this.queue.getElement(json.targetId);

		//make it position abs
		element.style.position='absolute';
		// The thing we target
		let dragElement = this.queue.getElement(json.dragTargetId);
		// Our bounds
		let boundsElement = this.queue.getElement(json.bounds);

		let bbox=boundsElement.getBoundingClientRect();
		//console.log(element.offsetLeft, element.offsetTop);
		this.drags[options.drag] = {
			pos: {x: element.offsetLeft, y: element.offsetTop,ox:0,oy:0},
			element: element,
			dragElement: dragElement,
			boundsElement: boundsElement,
			mode: true,
			boundary: {
				x:0,
				y:0,
				x1:bbox.width,
				y1:bbox.height
			},
			buffer:options.buffer
		};

		this.drags[options.drag].dimensions=element.getBoundingClientRect();
		dragElement.addEventListener('mousedown', dragMouseDown);
		window.addEventListener('resize', resize);

		function resize(e) {
			let bbox=boundsElement.getBoundingClientRect();
			self.drags[options.drag].boundary.x1=bbox.width;
			self.drags[options.drag].boundary.y1=bbox.height;
		}

		function dragMouseDown(e) {
			if (self.drags[options.drag].mode) {
				e = e || window.event;
				e.preventDefault();
				self.drags[options.drag].dimensions=element.getBoundingClientRect();

				self.drags[options.drag].pos.ox = e.clientX - self.drags[options.drag].pos.x ;
				self.drags[options.drag].pos.oy = e.clientY - self.drags[options.drag].pos.y;

				document.onmouseup = closeDragElement;
				document.onmousemove = elementDrag;
			}
		}

		function elementDrag(e) {
			e = e || window.event;
			e.preventDefault();
			//console.log(`mouse: ${e.clientX}:${e.clientY}`);

			let bboxX= e.clientX - self.drags[options.drag].pos.ox;
			let bboxY= e.clientY - self.drags[options.drag].pos.oy;
			// calculate the new cursor position:
			if(
				bboxX-options.buffer>=self.drags[options.drag].boundary.x
				&&(bboxX+options.buffer+self.drags[options.drag].dimensions.width)<=self.drags[options.drag].boundary.x1
				&&bboxY-options.buffer>=self.drags[options.drag].boundary.y
				&&(bboxY+options.buffer+self.drags[options.drag].dimensions.height)<=self.drags[options.drag].boundary.y1)
			{

				self.drags[options.drag].pos.x = bboxX;
				self.drags[options.drag].pos.y = bboxY;

				element.style.top = self.drags[options.drag].pos.y + "px";
				element.style.left = self.drags[options.drag].pos.x + "px";
			}

		}

		function closeDragElement() {
			/*
			 * End of drag so get rid of events
			 */
			document.onmouseup = null;
			document.onmousemove = null;
		}

		this.finished(pid, this.queue.DEFINE.FIN_OK);

	}
}

/** @module ourthings/Queueable/Files */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * File upload functions
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 */
export default class Files extends Queueable {


	/**
	 * Load a file into memory with option to preview
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom location that contains the files input
	 * @param {string} json.previewId - Dom location to put preview
	 * @param {string} json.background - Use background rather than src
	 */
	clientLoad(pid, json) {
		let self = this;
		let element = self.queue.getElement(json.targetId);
		let files = element.files;
		if (files && files[0]) {
			let reader = new FileReader();
			reader.readAsDataURL(files[0]);
			reader.onload = function (evt) {
				if (json.previewId) {
					let preview = self.queue.getElement(json.previewId);
					if(json.background)
						preview.style['background-image']=evt.target.result;
					else
						preview.src = evt.target.result;
				} else {
					self.set(pid, evt.target.result);
				}
				self.finished(pid, self.queue.DEFINE.FIN_OK);
			};
			reader.onerror = function (evt) {
				// TODO Write me!
				console.log('error');
				debugger;
				self.finished(pid, self.queue.DEFINE.FIN_ERROR);

			};
		}

	}

	/**
	 * Image preview
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom location that contains the files input
	 * @param {string} json.previewId - Dom location to put preview
	 * @param {string} json.dragTargetId - Dom location to target for the drop zone
	 * @param {string} json.background - Use background rather than src

	 */
	filePreview(pid, json) {
		let self = this;
		let options=Object.assign({
			"prefix":"main"
		},json);
		let element = self.queue.getElement(json.targetId);

		/**
		 *  Clear down any previous file memory entries
		 */
		self.queue.deleteMemory('file');

		/**
		 * Drag drop functions
		 * @param e
		 */
		function onDragEnter(e) {
			e.stopPropagation();
			e.preventDefault();
		}

		function onDragOver(e) {
			e.stopPropagation();
			e.preventDefault();
		}

		function onDragLeave(e) {
			e.stopPropagation();
			e.preventDefault();
		}

		function onDrop(e) {
			e.stopPropagation();
			e.preventDefault();
			setFiles(e.dataTransfer.files);
			return false;
		}

		function setFiles(files) {
			let file = files[0];
			self.queue.setMemory('file', file, 'Session');
			doPreview(file);
		}

		/**
		 *  Did we need a drag and drop on this element?
		 */
		if (json.dragTargetId) {
			let dragElement = self.queue.getElement(json.dragTargetId);
			dragElement.addEventListener('dragenter', onDragEnter, false);
			dragElement.addEventListener('dragover', onDragOver, false);
			dragElement.addEventListener('dragleave', onDragLeave, false);
			dragElement.addEventListener('drop', onDrop, false);
		}


		/**
		 *  Event for change (IE the input file box selected a file)
		 */
		element.addEventListener('change', function () {
			let file = this.files[0];
			self.queue.setMemory('file', file, 'Session');
			doPreview(file);
		});

		/**
		 * Actual preview function called from both change & drag drop events
		 * @param file
		 */

		function doPreview(file) {
			let reader = new FileReader();
			reader.readAsDataURL(file);
			reader.onload = function (evt) {
				self.queue.setMemory("filePreview", evt.target.result,"Session");
				self.queue.execute(options.prefix+"FilePreview");
			};
			reader.onerror = function (evt) {
				// TODO Write me!
				console.log('error');
				debugger;

			};
		}

		self.finished(pid, self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Upload fiel to S3
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom location that contains the files input
	 * @param {boolean} json.file - use the memory file object set
	 * @param {string} json.contentType - Content type of file
	 * @param {string} json.headers - Additional headers to send
	 * @param {string} json.url - url to upload to
	 */
	putFileToS3(pid, json) {
		let self = this;
		json.contentType = json.contentType || 'image/png';
		let headers = json.headers || {};
		headers['Content-Type'] = json.contentType;
		let file;
		/**
		 *  The upload can come from an input or via memory
		 *  In the case of memory we cleanup the memory
		 */
		if (json.file) {
			file=memory.file.value;
			self.queue.deleteMemory('file');
		} else {
			let element = self.queue.getElement(json.targetId);
			file = element.files[0];
		}

		/**
		 *  Make the call to S3 with the file data
		 */
		fetch(json.url, {
			headers: headers,
			method: 'PUT',
			body: file
		})
			.then(response => self.queue.handleFetchErrors(response))
			.then(function (response) {
				switch (json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid, response);
				self.finished(pid, self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("Warning this error is probably fatal as I have no templates to load");
				self.finished(pid, self.queue.DEFINE.FIN_ERROR, 'S3 upload error');

			});

	}
}

/** @module ourthings/Queueable/Geojson */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * geojson Functions
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 */
export default class Geojson extends Queueable {


	/**
	 * Init the geojson history
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.geojson - starting geojson
	 */
	historyInit(pid, json) {
		let options = Object.assign({
			"index": "feature_id",
			"mode": "simple"
		}, json);

		let history = {"revertPtr": false, "mode": options.mode, "log": []};
		this.index = options.index;

		history.log.push({
			"name": "Session start",
			"type": "full",
			"geojson": this._compressGeojson("full", options.geojson),
			"index": this._makeHistoryIndex(options.geojson)
		});
		this.queue.setMemory("geojsonHistory", history, "Session");
		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Add to the geojson history
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.geojson - starting geojson
	 * @param {string} json.type - Type of entry
	 */
	historyAdd(pid, json) {
		let options = Object.assign({
			"type": "incremental"
		}, json);
		if (memory.geojsonHistory.value.revertPtr!==false&&memory.geojsonHistory.value.mode === "simple") {
			memory.geojsonHistory.value.log=memory.geojsonHistory.value.log.splice(0,memory.geojsonHistory.value.revertPtr+1);
			memory.geojsonHistory.value.revertPtr=false;
		}

		let entry={
			"name": options.name,
			"geojson": this._compressGeojson(options.type, options.geojson),
			"type": options.type,
			"index": this._makeHistoryIndex(options.geojson),
			"details":options.details
		}
		memory.geojsonHistory.value.log.push(entry);

		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}

	/**
	 * We need to make a fast access index for history based on our 'index' element for use later to speed up
	 * access and prevent many searches
	 *
	 * @param json
	 * @private
	 */
	_makeHistoryIndex(json) {
		let index = {};
		for (let i in json.features) {
			index[json.features[i].properties[this.index]] = {"position": i};
		}
		return index;
	}

	_compressGeojson(type, json) {
		if (type === 'full' || type === 'incremental')
			return json;
		if (type === 'geometry') {
			/*
			 * This edit was geometry only so we nuke properties (but not index as we need that)
			 */
			for (let i in json.features) {
				for (let p in json.features[i].properties) {
					if (p !== this.index)
						delete json.features[i].properties[p];
				}
			}
		}
		return json;
	}

	historyRevert(pid, json) {
		/**
		 * Add to the geojson history
		 * @param {number} pid - Process ID
		 * @param {object} json - queue arguments
		 * @param {string} json.id - history id
		 * @param {string} json.name - memory name to use
		 */
		let currentPtr = parseInt(json.id);
		let historyCut = [];
		let geojsonBuild;
		let fastIndex;
		memory.geojsonHistory.value.revertPtr = currentPtr;
		/*
		 * Cut out history from our end id to the first full geojson
		 */
		for (let i = currentPtr; i >= 0; i--) {
			if (memory.geojsonHistory.value.log[i].type === "full") {
				geojsonBuild = this.queue.deepCopy(memory.geojsonHistory.value.log[i].geojson);
				fastIndex = this.queue.deepCopy(memory.geojsonHistory.value.log[i].index);
				break;
			}
			historyCut.unshift(memory.geojsonHistory.value.log[i]);
		}
		/*
		 * geojsonBuild now contains the last full backup & historyCut is the array of changes needed to be applies
		 * in order to achieve the point in history. fastIndex is the index to access geojsonBuild and needs updating as
		 * we move down the history *if* additions / subtactions are made
		 */
		let len = historyCut.length;
		for (let i = 0; i < len; i++) {
			for (let f = 0; f < historyCut[i].geojson.features.length; f++) {
				switch (historyCut[i].type) {
					case 'geometry':
						geojsonBuild.features[fastIndex[historyCut[i].geojson.features[f].properties[this.index]].position].geometry = this.queue.deepCopy(historyCut[i].geojson.features[f].geometry);
						break;
					default:
						geojsonBuild.features[fastIndex[historyCut[i].geojson.features[f].properties[this.index]].position] = this.queue.deepCopy(historyCut[i].geojson.features[f]);
						break;
				}
			}
		}
		self.queue.setMemory(json.name, geojsonBuild, "Session");
		if (memory.geojsonHistory.value.mode !== "simple") {
			memory.geojsonHistory.value.log.push({
				"name": "Revert point",
				"geojson": geojsonBuild,
				"type": "full",
				"index": this._makeHistoryIndex(geojsonBuild)

			})
		}

		this.finished(pid, this.queue.DEFINE.FIN_OK);

	}

	historyCompare(pid,json) {
		let options = Object.assign({
			"index": "feature_id"
		}, json);
		const index1=this._makeHistoryIndex(options.geojson1);
		const index2=this._makeHistoryIndex(options.geojson2);

		let deletes=[];

		//TODO iterate to find deletes

		this.queue.setMemory("historyCompare", {"updates":options.geojson2,"deletes":deletes}, "Session");

		this.finished(pid, this.queue.DEFINE.FIN_OK);
	}


}

/** @module ourthings/Queueable/Internals */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Internal queue functions exposed to queueables
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 *
 */
export default class Internals extends Queueable {

	/**
	 * Execute a prepared queue
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - prepared queue to call
	 * @param {boolean} json.silentFail - fail the queue silently?
	 * @param {object} [json.json] - New arguments to send to queue
	 * @example
	 * internals.execute({"name":"myQueue"});
	 */
	execute(pid,json) {
		let self=this;
		self.queue.execute(json.name,json.json,json.silentFail);
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Check a statement and run prepared queue if its true
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.statement - statement to check
	 * @param {string} json.name - prepared queue to call
	 * @param {string} json.else - prepared queue to call on else
	 * @param {object} [json.json] - New arguments to send to queue
	 * @example
	 * internals.ifqueue({"statement":"memory.loginAPI.value.token","name":"loggedIn"});
	 */
	ifqueue(pid,json) {
		let self=this;
		let evalRes;
		try {
			evalRes=eval(json.statement);
		} catch(e) {
			evalRes=false;
		}
		if(evalRes) {
			self.queue.execute(json.name,json.json);
		} else {
			if(json.else)
				self.queue.execute(json.else,json.json);

		}
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Set a memory value
	 *
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - name of memory item
	 * @param {*} json.value - value to set (can be any type)
	 * @param {string} [json.mode] - [Garbage|Session|Permanent] Memory mode
	 * @example
	 * internals.setMemory({"name":"test","mode":"Session","value":"Test String"});
	 * internals.setMemory({"name":"test","mode":"Session","value":{"trueFalse":[true,false],"objects":[{"ElementOne":"Result One"},{"ElementTwo":"Result Two"}]}});
	 */
	setMemory(pid,json) {
		let self=this;
		self.queue.setMemory(json.name,json.value,json.mode);
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * push a value to an array memory value
	 *
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - name of memory item
	 * @param {boolean} json.toggle - toggle unique items
	 * @param {*} json.value - value to set (can be any type)
	 * @param {string} [json.mode] - [Garbage|Session|Permanent] Memory mode
	 * @example
	 * internals.pushMemory({"name":"test","mode":"Session","value":"Test String"});
	 * internals.pushMemory({"name":"test","mode":"Session","value":{"trueFalse":[true,false],"objects":[{"ElementOne":"Result One"},{"ElementTwo":"Result Two"}]}});
	 */
	pushMemory(pid,json) {
		let self=this;
		let modArray=[];
		if(memory[json.name])
			modArray=memory[json.name].value;

		if(json.toggle===true) {
			let index=modArray.indexOf(json.value);
			if(index!==-1) {
				modArray.splice(index,1);
			} else
				modArray.push(json.value);
		} else
			modArray.push(json.value);
		self.queue.setMemory(json.name,modArray,json.mode);
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

    /**
     * Set a register
     * @param {int} pid - process ID
     * @param {object} json - queue arguments
     * @param {string} json.name - name of register
     * @example
     * internals.setRegister({"name":"test"});
     */
	setRegister(pid,json) {
        let self=this;
        self.queue.setRegister(json.name);
        self.finished(pid,self.queue.DEFINE.FIN_OK);
    }


	/**
	 * delete a register
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - name of register
	 * @example
	 * internals.deleteRegister({"name":"test"});
	 */
	deleteRegister(pid,json) {
		let self=this;
		self.queue.deleteRegister(json.name);
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Delete a memory value
	 *
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - name of memory item
	 * @param {*} json.value - value to set (can be any type)
	 * @param {string} [json.mode] - [Garbage|Session|Permanent] Memory mode
	 * @example
	 * internals.setMemory({"name":"test","mode":"Session","value":"Test String"});
	 * internals.setMemory({"name":"test","mode":"Session","value":{"trueFalse":[true,false],"objects":[{"ElementOne":"Result One"},{"ElementTwo":"Result Two"}]}});
	 */
	deleteMemory(pid,json) {
		this.queue.deleteMemory(json.name);
		this.finished(pid,this.queue.DEFINE.FIN_OK);
	}

	/**
	 * NOP - No operation
	 *
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @example
	 * internals.nop();
	 */
	nop(pid,json) {
		this.finished(pid,this.queue.DEFINE.FIN_OK);
	}
}

