*/

/*
*   @constructor MenuItem
*
*   @desc
*       Wrapper object for a simple menu item in a popup menu
*
*   @param domNode
*       The DOM element node that serves as the menu item container.
*       The menuObj PopupMenu is responsible for checking that it has
*       requisite metadata, e.g. role="menuitem".
*
*   @param menuObj
*       The object that is a wrapper for the PopupMenu DOM element that
*       contains the menu item DOM element. See PopupMenuAction.js
*/
let PopupMenuItem = function (domNode, popupMenuObj) {

	this.domNode   = domNode;
	this.popupMenu = popupMenuObj;

	this.keyCode = Object.freeze({
		'TAB': 9,
		'RETURN': 13,
		'ESC': 27,
		'SPACE': 32,
		'PAGEUP': 33,
		'PAGEDOWN': 34,
		'END': 35,
		'HOME': 36,
		'LEFT': 37,
		'UP': 38,
		'RIGHT': 39,
		'DOWN': 40
	});
};

PopupMenuItem.prototype.init = function () {
	this.domNode.tabIndex = -1;

	if (!this.domNode.getAttribute('role')) {
		this.domNode.setAttribute('role', 'menuitem');
	}

	this.domNode.addEventListener('keydown',    this.handleKeydown.bind(this));
	this.domNode.addEventListener('click',      this.handleClick.bind(this));
	this.domNode.addEventListener('focus',      this.handleFocus.bind(this));
	this.domNode.addEventListener('blur',       this.handleBlur.bind(this));
	this.domNode.addEventListener('mouseover',  this.handleMouseover.bind(this));
	this.domNode.addEventListener('mouseout',   this.handleMouseout.bind(this));

};

/* EVENT HANDLERS */

PopupMenuItem.prototype.handleKeydown = function (event) {
	let flag = false,
		char = event.key;
	function isPrintableCharacter (str) {
		return str.length === 1 && str.match(/\S/);
	}

	if (event.ctrlKey || event.altKey  || event.metaKey) {
		return;
	}

	if (event.shiftKey) {
		if (isPrintableCharacter(char)) {
			this.popupMenu.setFocusByFirstCharacter(this, char);
		}
	}
	else {

		switch (event.keyCode) {
			case this.keyCode.SPACE:
				flag = true;
				break;

			case this.keyCode.RETURN:
				this.handleClick(event);
				flag = true;
				break;

			case this.keyCode.ESC:
				this.popupMenu.setFocusToController();
				this.popupMenu.close(true);
				flag = true;
				break;

			case this.keyCode.UP:
				this.popupMenu.setFocusToPreviousItem(this);
				flag = true;
				break;

			case this.keyCode.DOWN:
				this.popupMenu.setFocusToNextItem(this);
				flag = true;
				break;

			case this.keyCode.HOME:
			case this.keyCode.PAGEUP:
				this.popupMenu.setFocusToFirstItem();
				flag = true;
				break;

			case this.keyCode.END:
			case this.keyCode.PAGEDOWN:
				this.popupMenu.setFocusToLastItem();
				flag = true;
				break;

			case this.keyCode.TAB:
				this.popupMenu.setFocusToController();
				this.popupMenu.close(true);
				break;

			default:
				if (isPrintableCharacter(char)) {
					this.popupMenu.setFocusByFirstCharacter(this, char);
				}
				break;
		}
	}

	if (flag) {
		event.stopPropagation();
		event.preventDefault();
	}
};

PopupMenuItem.prototype.handleClick = function (event) {
	let attr=event.target.getAttribute('data-queue');
	if(attr)
		queue.execute(attr);

	/*
	 * Is this a keyboard event? If so and the data-click attr is set we send a click to the element.
	 * This will trigger the ourthings event that is bound to it
	 */
	let click=event.target.getAttribute('data-click');
	if(click==="true"&&event.keyCode)
		event.target.click();
	this.popupMenu.setFocusToController();
	let noClose=event.target.getAttribute('data-noclose');
	if(!noClose)
		this.popupMenu.close(true)

};

PopupMenuItem.prototype.handleFocus = function (event) {
	this.popupMenu.hasFocus = true;
};

PopupMenuItem.prototype.handleBlur = function (event) {
	this.popupMenu.hasFocus = false;
	setTimeout(this.popupMenu.close.bind(this.popupMenu, false), 300);
};

PopupMenuItem.prototype.handleMouseover = function (event) {
	this.popupMenu.hasHover = true;
	this.popupMenu.open();

};

PopupMenuItem.prototype.handleMouseout = function (event) {
	this.popupMenu.hasHover = false;
	setTimeout(this.popupMenu.close.bind(this.popupMenu, false), 300);
};

