*
*   Desc:   Popup menu widget that implements ARIA Authoring Practices
*/

/*
*   @constructor PopupMenuAction
*
*   @desc
*       Wrapper object for a simple popup menu (without nested submenus)
*
*   @param domNode
*       The DOM element node that serves as the popup menu container. Each
*       child element of domNode that represents a menuitem must have a
*       'role' attribute with value 'menuitem'.
*
*   @param controllerObj
*       The object that is a wrapper for the DOM element that controls the
*       menu, e.g. a button element, with an 'aria-controls' attribute that
*       references this menu's domNode. See MenuButton.js
*
*       The controller object is expected to have the following properties:
*       1. domNode: The controller object's DOM element node, needed for
*          retrieving positioning information.
*       2. hasHover: boolean that indicates whether the controller object's
*          domNode has responded to a mouseover event with no subsequent
*          mouseout event having occurred.
*/
let PopupMenuAction = function (domNode, controllerObj) {
	let elementChildren,
		msgPrefix = 'PopupMenu constructor argument domNode ';

	// Check whether domNode is a DOM element
	if (!domNode instanceof Element) {
		throw new TypeError(msgPrefix + 'is not a DOM Element.');
	}

	// Check whether domNode has child elements
	if (domNode.childElementCount === 0) {
		throw new Error(msgPrefix + 'has no element children.');
	}

	// Check whether domNode child elements are A elements
	let childElement = domNode.firstElementChild;
	while (childElement) {
		let menuitem = childElement.firstElementChild;
		if (menuitem && menuitem === 'A') {
			throw new Error(msgPrefix + 'Cannot have descendant elements are A elements.');
		}
		childElement = childElement.nextElementSibling;
	}

	this.domNode = domNode;
	this.controller = controllerObj;

	this.menuitems  = [];      // see PopupMenu init method
	this.firstChars = [];      // see PopupMenu init method

	this.firstItem  = null;    // see PopupMenu init method
	this.lastItem   = null;    // see PopupMenu init method

	this.hasFocus   = false;   // see MenuItem handleFocus, handleBlur
	this.hasHover   = false;   // see PopupMenu handleMouseover, handleMouseout
};

/*
*   @method PopupMenuAction.prototype.init
*
*   @desc
*       Add domNode event listeners for mouseover and mouseout. Traverse
*       domNode children to configure each menuitem and populate menuitems
*       array. Initialize firstItem and lastItem properties.
*/
PopupMenuAction.prototype.init = function () {
	let childElement, menuElement, firstChildElement, menuItem, textContent, numItems, label;

	// Configure the domNode itself
	this.domNode.tabIndex = -1;

	this.domNode.setAttribute('role', 'menu');

	if (!this.domNode.getAttribute('aria-labelledby') && !this.domNode.getAttribute('aria-label') && !this.domNode.getAttribute('title')) {
		label = this.controller.domNode.innerHTML;
		this.domNode.setAttribute('aria-label', label);
	}

	this.domNode.addEventListener('mouseover', this.handleMouseover.bind(this));
	this.domNode.addEventListener('mouseout',  this.handleMouseout.bind(this));

	// Traverse the element children of domNode: configure each with
	// menuitem role behavior and store reference in menuitems array.
	let menuElements = this.domNode.getElementsByTagName('LI');

	for (let i = 0; i < menuElements.length; i++) {

		menuElement = menuElements[i];

		if (!menuElement.firstElementChild && menuElement.getAttribute('role') != 'separator') {
			menuItem = new PopupMenuItem(menuElement, this);
			menuItem.init();
			this.menuitems.push(menuItem);
			textContent = menuElement.textContent.trim();
			this.firstChars.push(textContent.substring(0, 1).toLowerCase());
		}
	}

	// Use populated menuitems array to initialize firstItem and lastItem.
	numItems = this.menuitems.length;
	if (numItems > 0) {
		this.firstItem = this.menuitems[0];
		this.lastItem  = this.menuitems[numItems - 1];
	}
};

/* EVENT HANDLERS */

PopupMenuAction.prototype.handleMouseover = function (event) {
	this.hasHover = true;
};

PopupMenuAction.prototype.handleMouseout = function (event) {
	this.hasHover = false;
	setTimeout(this.close.bind(this, false), 300);
};

/* FOCUS MANAGEMENT METHODS */

PopupMenuAction.prototype.setFocusToController = function (command) {
	if (typeof command !== 'string') {
		command = '';
	}

	if (command === 'previous') {
		this.controller.menubutton.setFocusToPreviousItem(this.controller);
	}
	else {
		if (command === 'next') {
			this.controller.menubutton.setFocusToNextItem(this.controller);
		}
		else {
			this.controller.domNode.focus();
		}
	}
};

PopupMenuAction.prototype.setFocusToFirstItem = function () {
	this.firstItem.domNode.focus();
};

PopupMenuAction.prototype.setFocusToLastItem = function () {
	this.lastItem.domNode.focus();
};

PopupMenuAction.prototype.setFocusToPreviousItem = function (currentItem) {
	let index;

	if (currentItem === this.firstItem) {
		this.lastItem.domNode.focus();
	}
	else {
		index = this.menuitems.indexOf(currentItem);
		this.menuitems[index - 1].domNode.focus();
	}
};

PopupMenuAction.prototype.setFocusToNextItem = function (currentItem) {
	let index;

	if (currentItem === this.lastItem) {
		this.firstItem.domNode.focus();
	}
	else {
		index = this.menuitems.indexOf(currentItem);
		this.menuitems[index + 1].domNode.focus();
	}
};

PopupMenuAction.prototype.setFocusByFirstCharacter = function (currentItem, char) {
	let start, index;
	char = char.toLowerCase();

	// Get start index for search based on position of currentItem
	start = this.menuitems.indexOf(currentItem) + 1;
	if (start === this.menuitems.length) {
		start = 0;
	}

	// Check remaining slots in the menu
	index = this.getIndexFirstChars(start, char);

	// If not found in remaining slots, check from beginning
	if (index === -1) {
		index = this.getIndexFirstChars(0, char);
	}

	// If match was found...
	if (index > -1) {
		this.menuitems[index].domNode.focus();
	}
};

PopupMenuAction.prototype.getIndexFirstChars = function (startIndex, char) {
	for (let i = startIndex; i < this.firstChars.length; i++) {
		if (char === this.firstChars[i]) {
			return i;
		}
	}
	return -1;
};

/* MENU DISPLAY METHODS */

PopupMenuAction.prototype.open = function () {
	// get bounding rectangle of controller object's DOM node
	let rect = this.controller.domNode.getBoundingClientRect();

	// set CSS properties
	this.domNode.style.display = 'block';
	this.domNode.style.position = 'absolute';
	//this.domNode.style.top  = rect.height + 'px';
	//this.domNode.style.left = '0px';

	// set aria-expanded attribute
	this.controller.domNode.setAttribute('aria-expanded', 'true');
	if( this.class) {
		this.domNode.classList.add(this.class);
	}
};

PopupMenuAction.prototype.close = function (force) {
	if (typeof force !== 'boolean') {
		force = false;
	}

	if (force || (!this.hasFocus && !this.hasHover && !this.controller.hasHover)) {
		this.domNode.style.display = 'none';
		this.controller.domNode.removeAttribute('aria-expanded');
		if( this.class) {
			this.domNode.classList.remove(this.class);
		}
	}


};

/** @module ourthings/Queueable */

/**
 * @classdesc
 *
 * The base class for queueable things
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * // queue = new Queue();
 *
 */
class Queueable {

	/**
	 * Constructor, Sets our status to be false (flipped on init)
	 *
	 */
	constructor() {
		this.ready=false;
	}

	/**
	 * init, override this for any promise based inits setting
	 * the self.ready=true after the promise
	 *
	 */
	init(queue) {
		this.queue=queue;
		this.ready=true;
	}

	/**
	 * Called from queue, starts running the actual command
	 * @param pid
	 * @param command
	 * @param json
	 */
	start(pid,command,json) {
		let self=this;
		if(self[command]&&typeof self[command] === 'function') {
			/*
			 * Pass the json through the var processor
			 */
			json=JSON.parse(self.queue.templateVars(JSON.stringify(json)));
			/*
			 * Process pointers IE *memory.foo.value
			 */
			const pointerRegex=/^\*(.*)$/;
			this.queue.objectMap(json,function(item){
				let match;
				if(match = pointerRegex.exec(item)) {
					try {
						item = eval(match[1]);
					} catch(e) {
						return undefined;
					}
				}
				return item;
			})

			/*
			 * Execute
			 */
			try {
				self[command](pid, json);
			} catch(e) {
				/*
				 * In an error state, we will fire up the debugger is they have console open
				 */
				console.log(e);
				self.queue.setMemory('generalErrorMessage',e,"Session");
				self.queue.execute('generalError');
				self.queue.finished(pid,self.queue.DEFINE.FIN_ERROR,`Queue [${command}] errored: ${e}`);
			}
		} else {
			self.queue.finished(pid,self.queue.DEFINE.FIN_ERROR,'No such command ['+command+']');
		}
	}

	/**
	 * Call this method after you command has finished. Failure to do so will result is
	 * a stalled queue
	 * @param pid
	 * @param mode
	 */
	finished(pid,mode,error='') {
		let self=this;
		/*
		 * a -1 pid is called via direct run and so not from our queue
		 */
		if(pid!==-1)
			self.queue.finished(pid,mode,error);
	}

	set(pid,value) {
		let self=this;
		self.queue.memory(pid,value);
	}
}

export default Queueable;

/** @module Validate */


/**
 * @classdesc
 *
 * Our validate class
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * // validate = new Validate();
 *
 */
class Validate {
	valid(item,args) {
		return true;
	}

}

class ValidateEmail extends Validate {
	valid(item,args) {
		let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
		return re.test(String(item).toLowerCase());
	}
}

class ValidateText extends Validate {
	valid(item,args) {
		return item.length>3;
	}
}

class ValidatePassword extends Validate {
	valid(item,args) {
		return item.length>=8;
	}
}

export {Validate,ValidateEmail,ValidateText,ValidatePassword};
