/** @module Api */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Dom Elements manipulations
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 */
export default class Api extends Queueable {

	/**
	 * Make a GET request
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.url - URL to make GET request to
	 * @param {string} json.contentType=application/json - Content type to request
	 * @param {string} json.header - header object to send (note Content-Type is overwritten by above setting)
	 */
	get(pid,json) {
		let self=this;
		json.contentType=json.contentType||'application/json';
		let headers=json.headers||{};
		headers['Content-Type']=json.contentType||'application/json';
		fetch(json.url, {
			headers: headers,
		})
			.then(response => self.queue.handleFetchErrors(response))
			.then(function(response) {
				switch(json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid,response);
				self.finished(pid,self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("Warning this error is probably fatal as I have no templates to load")
			});

	}

	/**
	 * Make a POST request
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.url - URL to make GET request to
	 * @param {string} json.contentType=application/json - Content type to request
	 * @param {string} json.header - header object to send (note Content-Type is overwritten by above setting)
	 * @param {string} json.body - object to send JSON.stringify is applies to this
	 */
	post(pid,json) {
		let self=this;
		json.contentType=json.contentType||'application/json';
		let headers=json.headers||{};
		headers['Content-Type']=json.contentType||'application/json';
		fetch(json.url, {
			headers: headers,
			method: 'POST',
			body: JSON.stringify(json.body)
		})
			.then(response => self.queue.handleFetchErrors(response))
			.then(function(response) {
				switch(json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid,response);
				self.finished(pid,self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("Warning this error is probably fatal as I have no templates to load")
			});

	}

	/**
	 * Make a PUT request
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.url - URL to make PUT request to
	 * @param {string} json.contentType=application/json - Content type to request
	 * @param {string} json.header - header object to send (note Content-Type is overwritten by above setting)
	 * @param {string} json.body - object to send JSON.stringify is applies to this
	 */
	put(pid,json) {
		let self=this;
		json.contentType=json.contentType||'image/png';
		let headers=json.headers||{};
		headers['Content-Type']=json.contentType;
		fetch(json.url, {
			headers: headers,
			method: 'PUT',
			body: window.atob(json.body)
		})
			.then(response => self.queue.handleFetchErrors(response))
			.then(function(response) {
				switch(json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid,response);
				self.finished(pid,self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("Warning this error is probably fatal as I have no templates to load")
			});

	}
}

/** @module ourthings/Queueable/Browser */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Browser Functions
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 */
export default class Browser extends Queueable {


	/**
	 * Redirect browser to a new page
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.location - Dom location to direct to
	 */
	redirect(pid,json) {
		window.top.location = json.location;
		this.finished(pid,this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Start the history monitor.
	 *
	 * Detects when the user navigates via back / forward button and runs queue based on history item
	 * added via addHistory
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 */
	initHistory(pid,json) {
		window.onpopstate = function(event) {
			let baseURL = location.href;
			let match=baseURL.match(/#(.*)/);
			if(match&&match[1]) {
				this.queue.execute('history'+match[1]);
			} else {
				this.queue.execute('historyRoot');
			}
		}
		this.finished(pid,this.queue.DEFINE.FIN_OK);

	}

	/**
	 * Add a history entry
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.history - history name to add
	 */
	addHistory(pid,json) {
		let options=Object.assign({
			"history":"Root",
		},json);
		let baseURL = location.href;
		if(baseURL.match(/#/))
			baseURL = baseURL.slice(0, location.href.indexOf('#'));
		location.href = baseURL + '#' + options.history;
		this.finished(pid,this.queue.DEFINE.FIN_OK);
	}
}

/** @module ourthings/Queueable/Elements */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Dom Elements manipulations
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * elements.removeClass({"targetId":".leftPanel","class":"hidden"});
 *
 */
export default class Elements extends Queueable {

	/**
	 * Add a class to a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.iframeId - iframe target
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.class - Name of class to add
	 *
	 * @example
	 * elements.addClass({"targetId":".leftPanel","class":"hidden"});
	 */
	addClass(pid,json) {
		let self=this;
		let elements=self.queue.getIframeElements(json.iframeId,json.targetId);
		self.set(pid,json);

		if(elements!==false) {
			elements.forEach(function(element) {
				element.classList.add(json.class);
			});
			self.finished(pid,self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid,self.queue.DEFINE.FIN_WARNING,'Could not add class ['+json.class+'] to ['+json.targetId+']');
		}
	}

	/**
	 * Remove a class to a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.iframeId - iframe target
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.class - Name of class to remove
	 *
	 * @example
	 * elements.removeClass({"targetId":".leftPanel","class":"hidden"});
	 */
	removeClass(pid,json) {
		let self=this;
		let elements=self.queue.getIframeElements(json.iframeId,json.targetId);
		self.set(pid,json);
		if(elements!==false) {
			elements.forEach(function(element) {
				element.classList.remove(json.class);
			});
			self.finished(pid,self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid,self.queue.DEFINE.FIN_WARNING,'Could not remove class ['+json.class+'] to ['+json.targetId+']');
		}
	}

	/**
	 * toggle a class on a dom element
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.class - Name of class to toggle
	 *
	 * @example
	 * elements.toggleClass({"targetId":".leftPanel","class":"hidden"});
	 */
	toggleClass(pid,json) {
		let self=this;
		let elements=self.queue.getIframeElements(json.iframeId,json.targetId);
		self.set(pid,json);
		if(elements!==false) {
			elements.forEach(function(element) {
				if (element.classList.contains(json.class))
					element.classList.remove(json.class);
				else
					element.classList.add(json.class);
			});
			self.finished(pid,self.queue.DEFINE.FIN_OK);
		} else {
			self.finished(pid,self.queue.DEFINE.FIN_WARNING,'Could not remove class ['+json.class+'] to ['+json.targetId+']');
		}
	}

	/**
	 * Cut a dom element out and paste it somewhere
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.sourceId - Source dom element
	 *
	 * @example
	 * elements.domCutPaste({"targetId":".leftPanel","sourceId":".thing"});
	 */
	domCutPaste(pid, json) {
		let source=self.queue.getElement(json.sourceId);
		let target=self.queue.getElement(json.targetId);
		target.appendChild(source);
		this.finished(pid,self.queue.DEFINE.FIN_OK);
	}

    /**
     * Set the HTML of an element
     * @param {number} pid - Process ID
     * @param {object} json - queue arguments
     * @param {string} json.targetId - Dom target
     * @param {string} json.html - HTML to add
     *
     * @example
     * elements.innerHTML({"targetId":".leftPanel","html":"Hello World"});

     */
	innerHTML(pid,json) {
		let self=this;
		let element=self.queue.getElement(json.targetId);
		element.innerHTML=json.html;
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Set the value of an input field
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 * @param {string} json.value - value to set
	 *
	 * @example
	 * elements.setInputValue({"targetId":"#username","value":"Hello World"});

	 */
	setInputValue(pid,json) {
		let self=this;
		let element=self.queue.getElement(json.targetId);
		element.value=json.value;
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Scroll to a dom target
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target
	 *
	 * @example
	 * elements.scrollIntoView({"targetId":"#ps_1"});

	 */
	scrollIntoView(pid,json) {
		let self=this;
		let element=self.queue.getElement(json.targetId);
		element.scrollIntoView();
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Scroll a dom container target to a dom target
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target to scroll to
	 * @param {string} json.containerId - Dom target container to scroll
	 *
	 * @example
	 * elements.scrollIntoView({"containerId":".map-elements-list","targetId":"#ps_1"});
	 */
	scrollContainerToElement(pid,json) {
		let self=this;
		let container = self.queue.getElement(json.containerId);
		let	element = self.queue.getElement(json.targetId);
		container.scrollTop = element.offsetTop;
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Monitor element(s) in a form and add classes on change
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - elements(s) to monitor for change and add modifiedClass to
	 * @param {string} json.buttonId - Element to add modifiedClass to
	 * @param {string} json.modifiedClass - Class to add to modified elements
	 * @param {string} json.modifiedQueue - Prepared queue to run when element modified
	 *
	 * @example
	 * elements.formActivityMonitor({"targetId":".functionMonitor","buttonId":".form-save","modifiedClass":"modified"});

	 */
	formActivityMonitor(pid,json) {
		let self=this;
		let elements=this.queue.getElements(json.targetId);
		let button;
		if(json.buttonId)
			button=this.queue.getElement(json.buttonId);
		elements.forEach(function(element) {
			element.addEventListener("change", function () {
				if(json.modifiedQueue)
					self.queue.execute(json.modifiedQueue,{});
				if(json.modifiedClass) {
					this.classList.add(json.modifiedClass);
					if(button)
						button.classList.add(json.modifiedClass);
				}
			});
			element.addEventListener("keypress", function () {
				if(json.modifiedQueue)
					self.queue.execute(json.modifiedQueue,{});
				if(json.modifiedClass) {
					this.classList.add(json.modifiedClass);
					if(button)
						button.classList.add(json.modifiedClass);
				}
			});

		});
		this.finished(pid,this.queue.DEFINE.FIN_OK);
	}
}

/** @module ourthings/Queueable/Files */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * File upload functions
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 */
export default class Files extends Queueable {


	/**
	 * Load a file into memory with option to preview
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom location that contains the files input
	 * @param {string} json.previewId - Dom location to put preview
	 */
	clientLoad(pid, json) {
		let self = this;
		let element = self.queue.getElement(json.targetId);
		let files = element.files;
		if (files && files[0]) {
			let reader = new FileReader();
			reader.readAsDataURL(files[0]);
			reader.onload = function (evt) {
				if (json.previewId) {
					let preview = self.queue.getElement(json.previewId);
					preview.src = evt.target.result;
				} else {
					self.set(pid, evt.target.result);
				}
				self.finished(pid, self.queue.DEFINE.FIN_OK);
			};
			reader.onerror = function (evt) {
				// TODO Write me!
				console.log('error');
				debugger;
				self.finished(pid, self.queue.DEFINE.FIN_ERROR);

			};
		}

	}

	/**
	 * Image preview
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom location that contains the files input
	 * @param {string} json.previewId - Dom location to put preview
	 * @param {string} json.dragTargetId - Dom location to target for the drop zone
	 */
	filePreview(pid, json) {
		let self = this;
		let element = self.queue.getElement(json.targetId);

		/**
		 *  Clear down any previous file memory entries
		 */
		self.queue.deleteMemory('file');

		/**
		 * Drag drop functions
		 * @param e
		 */
		function onDragEnter(e) {
			e.stopPropagation();
			e.preventDefault();
		}

		function onDragOver(e) {
			e.stopPropagation();
			e.preventDefault();
		}

		function onDragLeave(e) {
			e.stopPropagation();
			e.preventDefault();
		}

		function onDrop(e) {
			e.stopPropagation();
			e.preventDefault();
			setFiles(e.dataTransfer.files);
			return false;
		}

		function setFiles(files) {
			let file = files[0];
			self.queue.setMemory('file', file, 'Session');
			doPreview(file);
		}

		/**
		 *  Did we need a drag and drop on this element?
		 */
		if (json.dragTargetId) {
			let dragElement = self.queue.getElement(json.dragTargetId);
			dragElement.addEventListener('dragenter', onDragEnter, false);
			dragElement.addEventListener('dragover', onDragOver, false);
			dragElement.addEventListener('dragleave', onDragLeave, false);
			dragElement.addEventListener('drop', onDrop, false);
		}


		/**
		 *  Event for change (IE the input file box selected a file)
		 */
		element.addEventListener('change', function () {
			let file = this.files[0];
			self.queue.setMemory('file', file, 'Session');
			doPreview(file);
		});

		/**
		 * Actual preview function called from both change & drag drop events
		 * @param file
		 */

		function doPreview(file) {
			let reader = new FileReader();
			reader.readAsDataURL(file);
			reader.onload = function (evt) {
				if (json.previewId) {
					let preview = self.queue.getElement(json.previewId);
					preview.src = evt.target.result;
				} else {
					self.set(pid, evt.target.result);
				}
			};
			reader.onerror = function (evt) {
				// TODO Write me!
				console.log('error');
				debugger;

			};
		}

		self.finished(pid, self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Upload fiel to S3
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom location that contains the files input
	 * @param {boolean} json.file - use the memory file object set
	 * @param {string} json.contentType - Content type of file
	 * @param {string} json.headers - Additional headers to send
	 * @param {string} json.url - url to upload to
	 */
	putFileToS3(pid, json) {
		let self = this;
		json.contentType = json.contentType || 'image/png';
		let headers = json.headers || {};
		headers['Content-Type'] = json.contentType;
		let file;
		/**
		 *  The upload can come from an input or via memory
		 *  In the case of memory we cleanup the memory
		 */
		if (json.file) {
			file=memory.file.value;
			self.queue.deleteMemory('file');
		} else {
			let element = self.queue.getElement(json.targetId);
			file = element.files[0];
		}

		/**
		 *  Make the call to S3 with the file data
		 */
		fetch(json.url, {
			headers: headers,
			method: 'PUT',
			body: file
		})
			.then(response => self.queue.handleFetchErrors(response))
			.then(function (response) {
				switch (json.contentType) {
					case 'application/json':
						return response.json();
					default:
						return response.text();
				}
			})
			.then(function (response) {
				/*
				 * Convert the response to json and start the loader
				 */
				self.set(pid, response);
				self.finished(pid, self.queue.DEFINE.FIN_OK);

			})
			.catch(function (error) {
				console.info(self.queue.DEFINE.CONSOLE_LINE);
				console.error('Error:', error);
				console.info("Warning this error is probably fatal as I have no templates to load");
				self.finished(pid, self.queue.DEFINE.FIN_ERROR, 'S3 upload error');

			});

	}
}

/** @module ourthings/Queueable/Internals */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Internal queue functions exposed to queueables
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 *
 */
export default class Internals extends Queueable {

	/**
	 * Execute a prepared queue
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - prepared queue to call
	 * @param {object} [json.json] - New arguments to send to queue
	 * @example
	 * internals.execute({"name":"myQueue"});
	 */
	execute(pid,json) {
		let self=this;
		self.queue.execute(json.name,json.json);
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Check a statement and run prepared queue if its true
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.statement - statement to check
	 * @param {string} json.name - prepared queue to call
	 * @param {object} [json.json] - New arguments to send to queue
	 * @example
	 * internals.ifqueue({"statement":"memory.loginAPI.value.token","name":"loggedIn"});
	 */
	ifqueue(pid,json) {
		let self=this;
		if(eval(json.statement)) {
			self.queue.execute(json.name,json.json);

		}
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Set a memory value
	 *
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - name of memory item
	 * @param {*} json.value - value to set (can be any type)
	 * @param {string} [json.mode] - [Garbage|Session|Permanent] Memory mode
	 * @example
	 * internals.setMemory({"name":"test","mode":"Session","value":"Test String"});
	 * internals.setMemory({"name":"test","mode":"Session","value":{"trueFalse":[true,false],"objects":[{"ElementOne":"Result One"},{"ElementTwo":"Result Two"}]}});
	 */
	setMemory(pid,json) {
		let self=this;
		self.queue.setMemory(json.name,json.value,json.mode);
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Delete a memory value
	 *
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.name - name of memory item
	 * @param {*} json.value - value to set (can be any type)
	 * @param {string} [json.mode] - [Garbage|Session|Permanent] Memory mode
	 * @example
	 * internals.setMemory({"name":"test","mode":"Session","value":"Test String"});
	 * internals.setMemory({"name":"test","mode":"Session","value":{"trueFalse":[true,false],"objects":[{"ElementOne":"Result One"},{"ElementTwo":"Result Two"}]}});
	 */
	deleteMemory(pid,json) {
		this.queue.deleteMemory(json.name);
		this.finished(pid,this.queue.DEFINE.FIN_OK);

	}
}

/** @module Openlayers */
import Queueable from "../Queueable";
import {Map, View, Feature} from 'ol';
import {getWidth, getTopLeft} from 'ol/extent.js';

import TileLayer from 'ol/layer/Tile';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import {Point} from 'ol/geom.js';

import Disposable from 'ol/Disposable';
import OSM from 'ol/source/OSM';
import WMTS from 'ol/source/WMTS';
import WMTSTileGrid  from 'ol/tilegrid/WMTS';
import XYZ from 'ol/source/XYZ.js';

import WKT from 'ol/format/WKT';
import Overlay from 'ol/Overlay';
import {unByKey} from 'ol/Observable'


import GeoJSON from 'ol/format/GeoJSON';
import {fromLonLat,units,epsg3857,epsg4326} from 'ol/proj';
import Select from 'ol/interaction/Select.js';
import {click, pointerMove, altKeyOnly} from 'ol/events/condition.js';
import proj4 from "proj4";
import {register} from 'ol/proj/proj4';
import {get as getProjection} from 'ol/proj'

import {defaults as defaultInteractions, DragRotateAndZoom} from 'ol/interaction.js';

proj4.defs([
	["EPSG:27700", "+proj=tmerc +lat_0=49 +lon_0=-2 +k=0.999601 +x_0=400000 +y_0=-100000 +ellps=airy +towgs84=446.448,-125.157,542.060,0.1502,0.2470,0.8421,-20.4894 +datum=OSGB36 +units=m +no_defs"]
]);

register(proj4);




/**
 * @classdesc
 *
 * Openlayers Hook
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * //
 *
 * @description You need to add "ol": "^5.3.0" to your package.json to build with openlayers
 *
 */
export default class Openlayers extends Queueable {

	init(queue) {
		this.queue=queue;

		this.maps={};

		this.overlays={};

		this.ready=true;
	}

	/**
	 *
	 * Create a new map
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.map - name for the map (used to reference)
	 * @param {string} json.target - id of element in the page to target
	 * @param {int} json.zoom - statement to check
	 * @param {array} json.center - Center on
	 * @param {string} json.renderer - Renderers to use
	 * @example
	 * openlayer.addMap();
	 *
	 */
	addMap(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"zoom": 0,
			"renderer": ['webgl', 'canvas'],
			"target":"map",
			"center":[0,0],
			"projection": "EPSG:3857",
		},json);
		let projection =  getProjection(options.projection);
		const map = new Map({
			target: options.target,
			view: new View({
				center: options.center,
				zoom: options.zoom,
				renderer: options.renderer,
				projection: projection,
				resolutions: options.resolutions,
				extent: options.extent,
			}),
			interactions: defaultInteractions().extend([
				new DragRotateAndZoom()
			]),
			keyboardEventTarget: document

		});
		self.maps[options.map]={"object":map,"layers":{}};
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 *
	 * Add a layer to the map
	 * @param {int} pid - process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.map - name for the map (used to reference)
	 * @param {string} json.name - name for the layer (used to reference)
	 * @param {string} json.typr - Layer type osm,vector
	 * @param {float} json.opacity - layer opacity
	 * @param {boolean} json.transparent - is the layer transparent?
	 * @param {string} json.style - Style object to use
	 * @param {boolean} json.active - Is the layer active
	 * @param {object|string} json.geojson - geojson to add to the layer (vector)
	 * @example
	 * openlayer.addLayer();
	 *
	 */

	addLayer(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"name":"default",
			"opacity": 1,
			"transparent": false,
			"active": true
		},json);
		let map=self.maps[options.map].object;
		let olLayer = null;

		/*
		 * If we had a style specified then we need to check if it needs expanding
		 */
		if(options.style!==undefined&&typeof options.style!=='object')
			options.style=eval(options.style);

		/*
		 * Find the requested layer type as a function
		 */
		let layerFunction = self["_addLayer_" + options.type];
		if (typeof layerFunction === "function") {
			olLayer = layerFunction.apply(self, [options]);
		}
		else {
			self.finished(pid,self.queue.DEFINE.FIN_ERROR,"No add layer function for " + options.type);
			return false;
		}
		/*
		 * Add the layer and update the the maps object with the new layers
		 */
		map.addLayer(olLayer);
		self.maps[options.map].layers[options.name]=olLayer;
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Add an osm layer
	 * @param options
	 * @return {TileLayer}
	 * @private
	 */
	_addLayer_osm(options) {
		let olLayer=new TileLayer({
			source: new OSM()
		});
		return olLayer;
	}

	/**
	 * Add an wmts layer
	 * @param options
	 * @return {TileLayer}
	 * @private
	 */
	_addLayer_wmts(options) {
		let self=this;
		let map=self.maps[options.map].object;
		let view=map.getView();
		let source= new WMTS({
			url: options.url,
			layer: options.layer,
			matrixSet: options.matrixSet,
			format: 'image/png',
			crossOrigin: 'anonymous',
			projection: view.getProjection(),
			tileGrid: new WMTSTileGrid({
				resolutions: view.getResolutions(),
				matrixIds: options.matrix,
				origin: options.origin

			})
		});
		let olLayer = new TileLayer({
			extent: options.extent,
			opacity: options.opacity,
			visible: options.active,
			name: options.name,
			source: source
		});

		return olLayer;

	}

	/**
	 * Add an xyz layer
	 * @param options
	 * @return {TileLayer}
	 * @private
	 */
	_addLayer_xyz(options) {
		let source= new XYZ({
			url: options.url
		});
		let olLayer = new TileLayer({
			extent: options.extent,
			opacity: options.opacity,
			visible: options.active,
			name: options.name,
			source: source
		});
		return olLayer;
	}

	/**
	 * Add a vector layer
	 * @param options
	 * @return {VectorLayer}
	 * @private
	 */
	_addLayer_vector(options) {
		let self=this;
		let source={};
		let vectorSource;
		if(options.geojson!==undefined) {
			if(typeof options.geojson==='object') {
				source.features = (new GeoJSON({})).readFeatures(options.geojson, {featureProjection: self.maps[options.map].object.getView().getProjection()});
			} else {
				source.features = (new GeoJSON({})).readFeatures(eval(options.geojson), {featureProjection: self.maps[options.map].object.getView().getProjection()});
			}
		}
		vectorSource = new VectorSource(source);
		let olLayer = new VectorLayer({
			name: options.name,
			visible: options.active,
			source: vectorSource,
			style: options.style,
			opacity: options.opacity,
			selectable: options.selectable,
			hover: options.hover
		});
		return olLayer;
	}

	/**
	 * Use the standard openlayers select control
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map name
	 * @param {string} json.mode -  on|off
	 * @param {string} json.perfix - prefix to use
	 * @param {array} json.layers - layers to use
	 *
	 * @description This select control uses the default openlayers model. Useful for applications with no overlapping features. It does not support selecting hidden features
	 */
	simpleSelect(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"mode":"on",
			"prefix":""
		},json);
		if(options.layers) {
			for(let i in options.layers) {
				options.layers[i]=self.maps[options.map].layers[options.layers[i]];
			}
		}
		let map=self.maps[options.map].object;
		let selectObj=self.maps[options.map].selectObj;
		if(selectObj===undefined) {
			selectObj = new Select({"layers":options.layers});
			self.maps[options.map].selectObj=selectObj;
			selectObj.on('select', selectFunction);
		}
		if(options.mode==='on') {

			map.addInteraction(selectObj);
		} else {
			map.removeInteraction(selectObj);
		}

		function selectFunction(e) {
			self.queue.setMemory(options.prefix+'simpleSelect', e, "Session");
			self.queue.setMemory(options.map+'selectedFeatures', e.selected, "Session");

			if (e.deselected.length > 0 && e.selected.length === 0)
				self.queue.execute(options.prefix+"simpleDeselect");
			if (e.selected.length > 0)
				self.queue.execute(options.prefix+"simpleSelect");
		}

		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Use a filter object to locate features on a single layer
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map name
	 * @param {array} json.layer - layer to use
	 * @param {object} json.filter - Filter eg {"feature_id":1}
	 *
	 */
	findFeatures(pid,json) {
		let options=Object.assign({
			"map":"default",
			"layer":"default",
			"filter":{}
		},json);
		let foundFeatures=[];
		let layer=this.maps[options.map].layers[options.layer];
		let source=layer.getSource();
		let features=source.getFeatures();
		for(let i in features) {
			for(let f in options.filter) {
				let check=features[i].get(f);
				/*
				 * Note there can be differing types here, hence ==
				 */
				if(check==options.filter[f]) {
					foundFeatures.push(features[i]);
					this.queue.setMemory('findFeatures', foundFeatures, "Session");
					this.queue.setMemory(options.map+'selectedFeatures', foundFeatures, "Session");
				}
			}
		}
		this.finished(pid,this.queue.DEFINE.FIN_OK);
	}

	/**
	 * Use the standard click event
	 * @param pid
	 * @param json
	 *
	 * @description This select control uses the default openlayers model. Useful for applications with no overlapping features. It does not support selecting hidden features
	 */
	simpleClick(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"mode":"on",
			"prefix":""
		},json);
		let map=self.maps[options.map].object;
		if(options.mode==="on") {
			self.maps[options.map].clickTag=map.on('click', clickfunction);
		} else {
			console.log('off');
			unByKey(self.maps[options.map].clickTag);
		}

		function clickfunction(e) {
			console.log(e);
			self.queue.setMemory(options.prefix+'simpleClick', e, "Session");
			self.queue.execute(options.prefix+"simpleClick");
		}
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Convert a coordinate to WKT
	 * @param pid
	 * @param json
	 *
	 * @description Convert a coordinate to WKT
	 */
	coordinatesToWKT(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
		},json);
		let olGeom = new Point(options.coordinate);
		let format = new WKT();
		let wktRepresenation  = format.writeGeometry(olGeom);
		self.set(pid,{"wkt":wktRepresenation});
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Add a feature to the Map
	 * TODO: This is old code for getting something working. Needs functionising, not for production
	 * @param pid
	 * @param json
	 */
	addFeature(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"layer":"default",
			"values":{}
		},json);

		let map=self.maps[options.map].object;
		let layer=self.maps[options.map].layers[options.layer];
		let view=map.getView();
		let source=layer.getSource();
		console.log(layer);

		let projection = "EPSG:" + options.geometry.match(/SRID=(.*?);/)[1];
		let wkt = options.geometry.replace(/SRID=(.*?);/, '');

		let format = new WKT();
		let feature = format.readFeature(wkt);
		options.values.geometry = feature.getGeometry().transform(projection, view.getProjection().getCode());
		source.addFeature(new Feature(options.values));
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Add geojson features to a layer
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference
	 * @param {string} json.layer - Layer to get extent from
	 * @param {string} json.gejson - geojson
	 */
	addGeojson(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"layer":"default",
			"geojson":{}
		},json);
		let map=self.maps[options.map].object;
		let layer=self.maps[options.map].layers[options.layer];
		let source = layer.getSource();

		let view = map.getView();

		let features = new GeoJSON().readFeatures(options.geojson, {
			featureProjection: view.getProjection().getCode()
		});
		source.addFeatures(features);
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

    /**
     * Remove the data from a layer on the map.
     * @param pid
     * @param json
     * @param {string} json.map - Map reference
     * @param {string} json.layer - Layer to clear
     */
    clearLayer(pid, json) {
        let self=this;
        let options=Object.assign({
            "map":"default",
            "layer":"default"
        },json);
        let layer=self.maps[options.map].layers[options.layer];
        let source = layer.getSource();
        source.clear();
        self.finished(pid,self.queue.DEFINE.FIN_OK);

    }

    toggleLayer(pid,json) {
	    let options=Object.assign({
		    "map":"default",
		    "layer":"default"
	    },json);
	    let layer=this.maps[options.map].layers[options.layer];
	    layer.setVisible(!layer.getVisible());
	    this.finished(pid,self.queue.DEFINE.FIN_OK);

    }

	/**
	 *  Move the map so the cords are at the center
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference
	 * @param {string} json.coordinate - Coordinate to use
	 * @example
	 * openlayers.centerOnCoordinate({"coordinate":"{{!^JSON.stringify(memory.simpleSelect.value.selected[0].getGeometry().getCoordinates())}}"});
	 *
	 */
	centerOnCoordinate(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
		},json);
		/*
		 * Pull all our resources
		 */
		let map=self.maps[options.map].object;
		let view = map.getView();
		let size=map.getSize();
		view.centerOn(json.coordinate,size,[size[0]/2,size[1]/2]);
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Zoom a layer to the extent of its features (needs appropriate zoom levels to work well
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference
	 * @param {string} json.inc - Increment of he zoom EG 1|-1|2|-2|etc
	 * @param {string} json.delay - Delay period of the zoom in ms
	 * @example
	 * openlayers.animateZoom({"inc":"2});
	 */
	animateZoom(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"inc":+1,
			"delay":100
		},json);
		/*
		 * Pull all our resources
		 */
		let map=self.maps[options.map].object;
		let view = map.getView();
		/*
		 * Animate a zoom
		 */
		view.animate({zoom: view.getZoom() + options.inc,duration :options.delay});
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Zoom a layer to the extent of its features (needs appropriate zoom levels to work well
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference
	 * @param {string} json.layer - Layer to get extent from
	 * @example
	 * openlayers.zoomToLayerExtent({"map":"map_1","layer":"data"});
	 */
	zoomToLayerExtent(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"layer":"default"
		},json);
		/*
		 * Pull all our resources
		 */
		let map=self.maps[options.map].object;
		let view = map.getView();
		let layer=self.maps[options.map].layers[options.layer];
		let source = layer.getSource();
		/*
		 * Get the extent of the features and fit them
		 */
		let extent = source.getExtent();
		try {
			view.fit(extent, map.getSize());
		} catch(e) {
			/*
			 * Fitting when the layer is empty fill cause OL to error
			 */
		}

		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}
	/**
	 * Update size of map (in the event of resize or rotation this will fix it)
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference can be * and all maps will be targeted
	 * @example
	 * openlayers.updateSize({"map":"map_1"});
	 */
	updateSize(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
		},json);
		/*
		 * Pull all our resources
		 */
		if(json.map==="*") {
			for(let i in self.maps) {
				let map=self.maps[i].object;
				map.updateSize();
			}
		} else {
			let map = self.maps[options.map].object;
			map.updateSize();
		}
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}

	/**
	 * Add an overlay to the map
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference
	 * @param {string} json.overlay - Overlay reference to use
	 * @param {string} json.targetId - Dom element to use
	 * @param {string} json.coordinate - coordinate to place it (Event.coordinate) for clicks
	 * @example
	 * openlayers.addOverlay({"targetId":"#functionOverlay","coordinate":"{{!^JSON.stringify(memory.simpleSelect.value.mapBrowserEvent.coordinate)}}"});
	 */
	addOverlay(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"overlay":"default",
		},json);

		/*
		 * Pull all our resources
		 */
		let map=self.maps[options.map].object;

		/*
		 * Get the html element from the dom
		 */
		let element=self.queue.getElement(options.targetId);
		/*
		 * Make an overlay and add to the map
		 */
		let overlay = new Overlay({
			element: element,
			position: options.coordinate
		});
		map.addOverlay(overlay);
		/*
		 * Store the object for later (destroy)
		 */
		self.overlays[options.overlay]={"object":overlay};
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}

	/**
	 * Remove an overlay from the map
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference
	 * @param {string} json.overlay - Overlay reference to use
	 * @example
	 * openlayers.removeOverlay();
	 */
	removeOverlay(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"overlay":"default",
		},json);

		/*
		 * Pull all our resources
		 */
		let map=self.maps[options.map].object;

		map.removeOverlay(self.overlays[options.overlay].object);
		delete self.overlays[options.overlay];
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}
}

/** @module ourthings/Queueable/Templates */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Template manipulations
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * templates.render({"targetId":"content","template":"basic"});
 *
 */
class Templates extends Queueable {

	/**
	 * Render a template into the dom using the queues templateProcessor
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.template - dom id of template to use
	 * @param {string} [json.target] - dom id of render target
	 * @param {string} [json.mode] - "insert|append"
	 * @example
	 * templates.render({"targetId":"content","template":"basic"});
	 */
	render(pid,json) {
		let self=this;
		json.mode=json.mode||"insert";
		self.set(pid,json);
		if(!self.queue.templateProcessor(json.template,json.targetId,json.mode))
			self.finished(pid,self.queue.DEFINE.FIN_ERROR,'Could not render template');
		else
			self.finished(pid,self.queue.DEFINE.FIN_OK);
	}
}

export default Templates;

/** @module ourthings/Queueable/W3Menu */
import Queueable from "../Queueable";

/**
 * @classdesc W3C Menu implementation
 **
 * For queue intergration you can add 'data-queue' attribute to cause a menu item to run a prepared queue
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * 	w3menu.initMenu({"targetId":"#menubutton1"});
 *
 */
export default class W3Menu extends Queueable {

	/**
	 * Start the menu on assigned targetId
	 *
	 * @description W3C Menu implementation see: https://www.w3.org/TR/wai-aria-practices/examples/menu-button/menu-button-actions.html
	 *
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.targetId - Dom target of the w3c menu html
	 *
	 * @example
	 * w3menu.initMenu({"targetId":"#menubutton1"});
	 */
	initMenu(pid,json) {
		let self=this;
		let options=Object.assign({
			"hover":true
		},json);
		let element=self.queue.getElement(json.targetId);
		element.setAttribute("data-bound","true");
		let menubutton = new Menubutton(element);
		menubutton.init(options);
		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}
}

