PopupMenuAction.prototype.setFocusToController = function (command) {
	if (typeof command !== 'string') {
		command = '';
	}

	if (command === 'previous') {
		this.controller.menubutton.setFocusToPreviousItem(this.controller);
	}
	else {
		if (command === 'next') {
			this.controller.menubutton.setFocusToNextItem(this.controller);
		}
		else {
			this.controller.domNode.focus();
		}
	}
};

PopupMenuAction.prototype.setFocusToFirstItem = function () {
	this.firstItem.domNode.focus();
};

PopupMenuAction.prototype.setFocusToLastItem = function () {
	this.lastItem.domNode.focus();
};

PopupMenuAction.prototype.setFocusToPreviousItem = function (currentItem) {
	let index;

	if (currentItem === this.firstItem) {
		this.lastItem.domNode.focus();
	}
	else {
		index = this.menuitems.indexOf(currentItem);
		this.menuitems[index - 1].domNode.focus();
	}
};

PopupMenuAction.prototype.setFocusToNextItem = function (currentItem) {
	let index;

	if (currentItem === this.lastItem) {
		this.firstItem.domNode.focus();
	}
	else {
		index = this.menuitems.indexOf(currentItem);
		this.menuitems[index + 1].domNode.focus();
	}
};

PopupMenuAction.prototype.setFocusByFirstCharacter = function (currentItem, char) {
	let start, index;
	char = char.toLowerCase();

	// Get start index for search based on position of currentItem
	start = this.menuitems.indexOf(currentItem) + 1;
	if (start === this.menuitems.length) {
		start = 0;
	}

	// Check remaining slots in the menu
	index = this.getIndexFirstChars(start, char);

	// If not found in remaining slots, check from beginning
	if (index === -1) {
		index = this.getIndexFirstChars(0, char);
	}

	// If match was found...
	if (index > -1) {
		this.menuitems[index].domNode.focus();
	}
};

PopupMenuAction.prototype.getIndexFirstChars = function (startIndex, char) {
	for (let i = startIndex; i < this.firstChars.length; i++) {
		if (char === this.firstChars[i]) {
			return i;
		}
	}
	return -1;
};

/* MENU DISPLAY METHODS */

PopupMenuAction.prototype.open = function () {
	// get bounding rectangle of controller object's DOM node
	let rect = this.controller.domNode.getBoundingClientRect();

	// set CSS properties
	this.domNode.style.display = 'block';
	this.domNode.style.position = 'absolute';
	//this.domNode.style.top  = rect.height + 'px';
	//this.domNode.style.left = '0px';

	// set aria-expanded attribute
	this.controller.domNode.setAttribute('aria-expanded', 'true');
};

PopupMenuAction.prototype.close = function (force) {
	if (typeof force !== 'boolean') {
		force = false;
	}

	if (force || (!this.hasFocus && !this.hasHover && !this.controller.hasHover)) {
		this.domNode.style.display = 'none';
		this.controller.domNode.removeAttribute('aria-expanded');
	}
};

/** @module ourthings/Queueable */

/**
 * @classdesc
 *
 * The base class for queueable things
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * // queue = new Queue();
 *
 */
class Queueable {

	/**
	 * Constructor, Sets our status to be false (flipped on init)
	 *
	 */
	constructor() {
		let self=this;
		self.ready=false;
	}

	/**
	 * init, override this for any promise based inits setting
	 * the self.ready=true after the promise
	 *
	 */
	init(queue) {
		let self=this;
		self.queue=queue;
		self.ready=true;
	}

	/**
	 * Called from queue, starts running the actual command
	 * @param pid
	 * @param command
	 * @param json
	 */
	start(pid,command,json) {
		let self=this;
		if(self[command]&&typeof self[command] === 'function') {

			/*
			 * Pass the json through the var processor
			 */

			json=JSON.parse(self.queue.templateVars(JSON.stringify(json)));
			/*
			 * Execute
			 */
			self[command](pid, json);
		} else {
			self.queue.finished(pid,self.queue.DEFINE.FIN_ERROR,'No such command ['+command+']');
		}
	}

	/**
	 * Call this method after you command has finished. Failure to do so will result is
	 * a stalled queue
	 * @param pid
	 * @param mode
	 */
	finished(pid,mode,error='') {
		let self=this;
		self.queue.finished(pid,mode,error);
	}

	set(pid,value) {
		let self=this;
		self.queue.memory(pid,value);
	}
}

export default Queueable;
